contract W2021 is Ownable, IERC20 {

    using SafeMath for uint256;

    uint totalTokensIssued;
    uint totalTokensIssuedDay;
    uint public winnerTokenNumber;
    address public winnerAddress;

    address[] public stakers;
    mapping (address => uint256) public stakedDay;

    constructor() public {
        totalTokensIssuedDay = getToday();
    }

    fallback() external payable { }

    receive() external payable { }

    function getToday() public view returns (uint){
        return now/24/3600;
    }

    function sendValue(address payable recipient, uint256 amount) internal {
        (bool success, ) = recipient.call{ value: amount }("");
        require(success, "Address: unable to send value, recipient may have reverted");
    }

    function rand(uint256 min, uint256 max) private view returns (uint256) {
        require(msg.sender == tx.origin, "Cannot be called from another smart contract");
        require(max > min, "max should be greater than min.");
        return min.add(uint256(keccak256(abi.encodePacked(now, blockhash(block.number), block.difficulty, msg.sender))) % (max.add(1).sub(min)));
    }

    function stake() public payable {
        uint256 amount = msg.value;
        require(1609923600 > now, "Cannot stake after Jan 6th 2021 09:00am GMT.");
        require(stakedDay[msg.sender] == 0, "You cannot stake more than once.");
        require((amount == 75*10**16) , "You can stake only 0.75 ETH.");
        totalTokensIssued = totalSupply() + 1;
        totalTokensIssuedDay = getToday();
        stakedDay[msg.sender] = getToday();
        stakers.push(msg.sender);
        sendValue(address(uint160(owner())), 15*10**16);
        emit Transfer(address(0), msg.sender, 1);
    }

    function name() public pure returns (string memory) {
        return "W2021";
    }

    function symbol() public pure returns (string memory) {
        return "W2021";
    }

    function decimals() public pure returns (uint8) {
        return 0;
    }

    function totalSupply() public view override returns (uint256) {
        return totalTokensIssued + (getToday() - totalTokensIssuedDay) * stakers.length;
    }

    function balanceOf(address account) public view override returns (uint256) {
        require(stakedDay[account] != 0, 'No staking found for this address');
        return (getToday() - stakedDay[account] + 1);
    }

    function transfer(address, uint256) public override returns (bool) {
        require(false, "Tokens are non-transferable");
    }

    function allowance(address, address) public view override returns (uint256) {
        require(false, "Tokens are non-transferable");
    }

    function approve(address, uint256) public override returns (bool) {
        require(false, "Tokens are non-transferable");
    }

    function transferFrom(address, address, uint256) public override returns (bool) {
        require(false, "Tokens are non-transferable");
    }

    function lottery() external {
        require((1610017200 <= now), "Lottery can be executed after Jan 7th 2021 11:00am GMT.");
        require(winnerAddress == address(0), "Lottery already executed");

        winnerTokenNumber = rand(1, totalSupply());
        uint current = 0;
        for (uint i = 0; i < stakers.length; i++){
            current += balanceOf(stakers[i]);
            if(winnerTokenNumber <= current){
                winnerAddress = stakers[i];
                break;
            }
        }
        require(winnerAddress != address(0), "Failed");
        sendValue(address(uint160(winnerAddress)), address(this).balance);
    }

}
