    "contracts/interfaces/IFullBPool.sol": {
      "content": "// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity 0.6.12;

import "./IERC20Nameable.sol";

interface IFullBPool is IERC20Nameable {
    function rebind(
        address token,
        uint256 balance,
        uint256 denorm
    ) external;

    function setSwapFee(uint256 swapFee) external;

    function setPublicSwap(bool publicSwap) external;

    function bind(
        address token,
        uint256 balance,
        uint256 denorm
    ) external;

    function unbind(address token) external;

    function gulp(address token) external;

    function isBound(address token) external view returns (bool);

    function getBalance(address token) external view returns (uint256);

    function getSwapFee() external view returns (uint256);

    function isPublicSwap() external view returns (bool);

    function getDenormalizedWeight(address token)
        external
        view
        returns (uint256);

    function getTotalDenormalizedWeight() external view returns (uint256);

    function getSpotPrice(address tokenIn, address tokenOut)
        external
        view
        returns (uint256 spotPrice);

    function getSpotPriceSansFee(address tokenIn, address tokenOut)
        external view
        returns (uint spotPrice);

    // solhint-disable-next-line func-name-mixedcase
    function EXIT_FEE() external view returns (uint256);

    function calcPoolOutGivenSingleIn(
        uint256 tokenBalanceIn,
        uint256 tokenWeightIn,
        uint256 poolSupply,
        uint256 totalWeight,
        uint256 tokenAmountIn,
        uint256 swapFee
    ) external pure returns (uint256 poolAmountOut);

    function calcSingleInGivenPoolOut(
        uint256 tokenBalanceIn,
        uint256 tokenWeightIn,
        uint256 poolSupply,
        uint256 totalWeight,
        uint256 poolAmountOut,
        uint256 swapFee
    ) external pure returns (uint256 tokenAmountIn);

    function calcSingleOutGivenPoolIn(
        uint256 tokenBalanceOut,
        uint256 tokenWeightOut,
        uint256 poolSupply,
        uint256 totalWeight,
        uint256 poolAmountIn,
        uint256 swapFee
    ) external pure returns (uint256 tokenAmountOut);

    function calcPoolInGivenSingleOut(
        uint256 tokenBalanceOut,
        uint256 tokenWeightOut,
        uint256 poolSupply,
        uint256 totalWeight,
        uint256 tokenAmountOut,
        uint256 swapFee
    ) external pure returns (uint256 poolAmountIn);

    function getCurrentTokens() external view returns (address[] memory tokens);
}
"
    },
    "contracts/Faucet.sol": {
      "content": "pragma solidity ^0.6.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";

// This is NOT SAFE for production use since the code is broadcast to the mempool
// this is only for testnet and convenience.
contract Faucet is Ownable {
    using SafeERC20 for IERC20;

    event LogCashIn(bytes32 code, address to);

    mapping(bytes32=>uint256) private _redeemableCodes;
    IERC20 private _token;

    constructor(address tokenAddr) Ownable() public {
        _token = IERC20(tokenAddr);
    }

    function addCode(bytes32 codeHash, uint256 amount) onlyOwner public {
        _redeemableCodes[codeHash] = amount;
    }

    function cashIn(string memory code, address to) public {
        bytes32 hsh = keccak256(abi.encodePacked(code));
        uint256 amount = _redeemableCodes[hsh];
        require(amount > 0, "Faucet:Unknown code");
        _redeemableCodes[hsh] = 0;
        _token.safeTransfer(to, amount);
        emit LogCashIn(hsh, to);
    }
}
"
    },
    "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol": {
