// YOINK Piggybank Smart Contract
// Utilizes the power of ChainLink VRF ( https://docs.chain.link/docs/chainlink-vrf )
// https://yoink.rocks

contract Piggybank is VRFConsumerBase, Ownable {
    ERC20 public YNKInterface;
    address public VRFTxFeeWallet;
    uint256 public lastPrize;
    uint256 public VRFTxFee;
    address public YNK;
    bytes32 internal keyHash;
    uint256 internal fee;
    uint256 public randomResult; // this will return which person in the top holders list has won
    address public winner;
    constructor()
        public
        VRFConsumerBase(
            0xf0d54349aDdcf704F77AE15b96510dEA15cb7952, // VRF Coordinator
            0x514910771AF9Ca656af840dff83E8264EcF986CA // LINK Token
        )
    {
        keyHash = 0xAA77729D3466CA35AE8D28B3BBAC7CC36A5031EFDC430821C02BC31A238AF445;
        fee = 2 * 10**18; // 2 LINK for VRF Fees
        YNK = 0x87C00817ABe35eD4C093e59043fae488238d2F74; // YNK Contract Address
        VRFTxFeeWallet = 0xeccd4018DeD9c243ED40d824c2c1690eAB3Fec4F; // Used to cover LINK fees
    }

    function payoutWinner() public onlyOwner {
        require(
            ERC20(YNK).balanceOf(address(this)) > 0,
            "There is no YNK to be paid out"
        );
        
        uint256 piggybankValue = ERC20(YNK).balanceOf(address(this));
        address lastWinner;
        
        if (randomResult > 500) {
            // if the winner falls outside of the top 500 we are sending the token to a burn address
            lastWinner = address(0x0000000000000000000000000000000000000001);
        } else {
           lastWinner = winner;
        }
        
        // prize is 1% of current holdings
        lastPrize = piggybankValue.sub(piggybankValue.mul(99).div(100));
        // VRF Tx Fee is 5% of prize
        VRFTxFee = lastPrize.sub(lastPrize.mul(95).div(100));
        
        lastPrize = lastPrize.sub(VRFTxFee);
        
        // let's transfer the deduction to cover ChainLink's VRF fees
        ERC20(YNK).transfer(VRFTxFeeWallet, VRFTxFee);
        // let's transfer the prize to the winner
        ERC20(YNK).transfer(lastWinner, lastPrize);
    }

    function getRandomNumber() public onlyOwner returns (bytes32 requestId){
        return requestRandomness(keyHash, fee, 1337);
    }
    
    function getPotBalance() public view returns (uint256 balance) {
       return ERC20(YNK).balanceOf(address(this));
    }
    
    function setTxFeeFundAddress(address wallet) public onlyOwner{
       VRFTxFeeWallet = wallet;
    }
    
    function emergencyWithdraw() public onlyOwner {
        uint256 totalBalance = ERC20(YNK).balanceOf(address(this));
        ERC20(YNK).transfer(VRFTxFeeWallet, totalBalance);
    }
    
    function setWinner(address _winner) public onlyOwner {
      winner = _winner;
    }
    
    function getBalance(address wallet) public view returns (uint256 balance) {
       return ERC20(YNK).balanceOf(wallet);
    }

    function fulfillRandomness(bytes32 requestId, uint256 randomness) internal override {
        randomResult = randomness % 1000 - 100;
    }
}
