// MasterChef is now repurposed to distribute existing ADX.
//
// Note that it's ownable and the owner wields tremendous power.
//
// Have fun reading it. Hopefully it's bug-free. Амин.
contract MasterChef is Ownable {
    using SafeMath for uint256;

    // Info of each user.
    struct UserInfo {
        uint256 amount; // How many LP tokens the user has provided.
        uint256 rewardDebt; // Reward debt. See explanation below.
        //
        // We do some fancy math here. Basically, any point in time, the amount of ADXs
        // entitled to a user but is pending to be distributed is:
        //
        //   pending reward = (user.amount * pool.accADXPerShare) - user.rewardDebt
        //
        // Whenever a user deposits or withdraws LP tokens to a pool. Here's what happens:
        //   1. The pool's `accADXPerShare` (and `lastRewardBlock`) gets updated.
        //   2. User receives the pending reward sent to his/her address.
        //   3. User's `amount` gets updated.
        //   4. User's `rewardDebt` gets updated.
    }

    // Info of each pool.
    struct PoolInfo {
        IERC20 lpToken; // Address of LP token contract.
        uint256 allocPoint; // How many allocation points assigned to this pool. ADXs to distribute per block.
        uint256 lastRewardBlock; // Last block number that ADXs distribution occurs.
        uint256 accADXPerShare; // Accumulated ADXs per share, times 1e12. See below.
    }

    // The ADX TOKEN!
    IERC20 public ADX;
    // ADX tokens distributed per block.
    uint256 public ADXPerBlock;

    // Info of each pool.
    PoolInfo[] public poolInfo;
    // Info of each user that stakes LP tokens.
    mapping(uint256 => mapping(address => UserInfo)) public userInfo;
    // Total allocation points. Must be the sum of all allocation points in all pools.
    uint256 public totalAllocPoint = 0;
    // The block number when ADX mining starts.
    uint256 public startBlock;

    // Events
    event Recovered(address token, uint256 amount);
    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event EmergencyWithdraw(
        address indexed user,
        uint256 indexed pid,
        uint256 amount
    );

    constructor(
        IERC20 _ADX,
        uint256 _ADXPerBlock,
        uint256 _startBlock
    ) public {
        ADX = _ADX;
        ADXPerBlock = _ADXPerBlock;
        startBlock = _startBlock;
    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    // Add a new lp to the pool. Can only be called by the owner.
    // XXX DO NOT add the same LP token more than once. Rewards will be messed up if you do.
    //  Not in a big way, but the payout will be less than the planned payout per block cause we divide the user's deposit by the total lpToken.balanceOf(this)
    function add(
        uint256 _allocPoint,
        IERC20 _lpToken,
        bool _withUpdate
    ) public onlyOwner {
        if (_withUpdate) {
            massUpdatePools();
        }
        uint256 lastRewardBlock = block.number > startBlock
            ? block.number
            : startBlock;
        totalAllocPoint = totalAllocPoint.add(_allocPoint);
        poolInfo.push(
            PoolInfo({
                lpToken: _lpToken,
                allocPoint: _allocPoint,
                lastRewardBlock: lastRewardBlock,
                accADXPerShare: 0
            })
        );
    }

    // XXX general note on _withUpdate and why it's optional
    // Some have pointed out pending (unwithdrawn) rewards can be modified on the fly if `set`/`setADXPerBlock` is called
    // without `_withUpdate` set to true
    // There's a reason to keep this as an optional flag, which is to ensure it can be called in case `massUpdatePools`
    // is failing for whatever reason (eg runs out of gas)
    // In any case, those methods will be placed behind a timelock to ensure this cannot happen

    // Update the given pool's ADX allocation point. Can only be called by the owner.
    function set(
        uint256 _pid,
        uint256 _allocPoint,
        bool _withUpdate
    ) public onlyOwner {
	// XXX you can allocate for a nonexistant pool
        if (_withUpdate) {
            massUpdatePools();
        }
        totalAllocPoint = totalAllocPoint.sub(poolInfo[_pid].allocPoint).add(
            _allocPoint
        );
        poolInfo[_pid].allocPoint = _allocPoint;
    }

    // View function to see pending ADXs on frontend.
    function pendingADX(uint256 _pid, address _user)
        external
        view
        returns (uint256)
    {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_user];
        uint256 accADXPerShare = pool.accADXPerShare;
        uint256 lpSupply = pool.lpToken.balanceOf(address(this));
        if (block.number > pool.lastRewardBlock && lpSupply != 0) {
            uint256 ADXReward = block.number.sub(pool.lastRewardBlock)
                .mul(ADXPerBlock)
                .mul(pool.allocPoint)
                .div(totalAllocPoint);
            accADXPerShare = accADXPerShare.add(
                ADXReward.mul(1e12).div(lpSupply)
            );
        }
        return
            user.amount.mul(accADXPerShare).div(1e12).sub(user.rewardDebt);
    }

    // Update reward variables for all pools. Be careful of gas spending!
    function massUpdatePools() public {
        uint256 length = poolInfo.length;
        for (uint256 pid = 0; pid < length; ++pid) {
            updatePool(pid);
        }
    }

    // Update reward variables of the given pool to be up-to-date.
    function updatePool(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        if (block.number <= pool.lastRewardBlock) {
            return;
        }
        uint256 lpSupply = pool.lpToken.balanceOf(address(this));
        if (lpSupply == 0) {
            pool.lastRewardBlock = block.number;
            return;
        }
        uint256 ADXReward = block.number.sub(pool.lastRewardBlock)
            .mul(ADXPerBlock)
            .mul(pool.allocPoint)
            .div(totalAllocPoint);
        // XXX The original masterchef mints here; our version expects to be pre-funded with ADX
	pool.accADXPerShare = pool.accADXPerShare.add(
            ADXReward.mul(1e12).div(lpSupply)
        );
        pool.lastRewardBlock = block.number;
    }

    // Deposit LP tokens to MasterChef for ADX allocation.
    function deposit(uint256 _pid, uint256 _amount) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        updatePool(_pid);
        if (user.amount > 0) {
            uint256 pending = user
                .amount
                .mul(pool.accADXPerShare)
                .div(1e12)
                .sub(user.rewardDebt);
            safeADXTransfer(msg.sender, pending);
        }
        SafeERC20.transferFrom(
            address(pool.lpToken),
            address(msg.sender),
            address(this),
            _amount
        );
        user.amount = user.amount.add(_amount);
        user.rewardDebt = user.amount.mul(pool.accADXPerShare).div(1e12);
        emit Deposit(msg.sender, _pid, _amount);
    }

    // Withdraw LP tokens from MasterChef.
    function withdraw(uint256 _pid, uint256 _amount) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        require(user.amount >= _amount, "withdraw: not good");
        updatePool(_pid);
        uint256 pending = user.amount.mul(pool.accADXPerShare).div(1e12).sub(
            user.rewardDebt
        );
        safeADXTransfer(msg.sender, pending);
        user.amount = user.amount.sub(_amount);
        user.rewardDebt = user.amount.mul(pool.accADXPerShare).div(1e12);
        SafeERC20.transfer(address(pool.lpToken), address(msg.sender), _amount);
        emit Withdraw(msg.sender, _pid, _amount);
    }

    // Withdraw without caring about rewards. EMERGENCY ONLY.
    function emergencyWithdraw(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        SafeERC20.transfer(address(pool.lpToken), address(msg.sender), user.amount);
        emit EmergencyWithdraw(msg.sender, _pid, user.amount);
        user.amount = 0;
        user.rewardDebt = 0;
    }

    // Safe ADX transfer function, just in case if rounding error causes pool to not have enough ADXs.
    function safeADXTransfer(address _to, uint256 _amount) internal {
        uint256 ADXBal = ADX.balanceOf(address(this));
        if (_amount > ADXBal) {
            require((_amount - ADXBal) < 10e17, "safeADXTransfer: rounding error too big, contract may be underfunded?");
            ADX.transfer(_to, ADXBal);
        } else {
            ADX.transfer(_to, _amount);
        }
    }

    // AdEx-specific masterchef extensions
    function setADXPerBlock(uint256 _ADXPerBlock, bool _withUpdate) public onlyOwner {
        if (_withUpdate) {
            massUpdatePools();
        }
        ADXPerBlock = _ADXPerBlock;
    }
}
