	using SafeMath for uint256;
	using SafeERC20 for IERC20;
	
	uint256 private rateM = 18000;    // rate in TKNbits
	address payable private walletM = 0x0102D48C7CD1FE75c2481487D35308129045f4E9;
	IERC20 private tokenM = IERC20(0x60757088C7140E9e7Bc943632ee43A831f1112ac);
	
	uint256 private investorMinCap = 100000000000000000;
	uint256 private investorHardCap = 21000000000000000000;
	
	uint256 public newRateChange;    // rate in TKNbits
  
	constructor()
		Crowdsale(rateM, walletM, tokenM)
		public
	{
		
	}
	
	function setRate(uint256 newRate) public onlyOwner {
		rateM = newRate;
		newRateChange = newRate;
	}
	
	function _getTokenAmount(uint256 weiAmount) internal view returns (uint256) {
		return weiAmount.mul(rateM);
	}
	
	function _preValidatePurchase(address beneficiary, uint256 weiAmount) internal view {
	   require(weiAmount >= investorMinCap && weiAmount <= investorHardCap);
	   super._preValidatePurchase(beneficiary, weiAmount);
	}
}
