// File: contracts/Sav3BalanceUtils.sol

pragma solidity ^0.5.17;

/* this contract is a read only utility to calculate sav3 balances faster on the website */

contract Sav3BalanceUtils {
    using SafeMath for uint256;

    ITokenUtils public sav3 = ITokenUtils(0x6E10AAcb89A28d6FA0FE68790777fec7E7f01890);
    ITokenUtils public uniswapPair = ITokenUtils(sav3.uniswapV2Pair());
    address public lockedTreasury = 0xE9102d0db9DFBDA79335bC95Ea53174064dBA0A1;

    function sav3Balance(address _user) public view returns (uint256) {
        return sav3.balanceOf(_user);
    }

    function sav3BalanceInUniswap(address _user) public view returns (uint256) {
        uint256 uniswapPairSav3Balance = sav3.balanceOf(address(uniswapPair));
        if (uniswapPairSav3Balance == 0) {
            return 0;
        }
        uint256 userLpBalance = uniswapPair.balanceOf(_user);
        if (userLpBalance == 0) {
            return 0;
        }
        uint256 userPercentOfLpOwned = userLpBalance.mul(1e12).div(uniswapPair.totalSupply());
        return uniswapPairSav3Balance.mul(userPercentOfLpOwned).div(1e12);
    }

    function sav3BalanceAll(address _user) public view returns (uint256) {
        return sav3Balance(_user).add(sav3BalanceInUniswap(_user));
    }

    function balanceOf(address _user) external view returns (uint256) {
        return sav3BalanceAll(_user);
    }

    function circulatingSupply() public view returns (uint256) {
        uint256 lockedSupply = sav3.lockedSupply();
        uint256 lockedTreasuryBalance = sav3.balanceOf(lockedTreasury);
        return sav3.totalSupply().sub(lockedSupply).sub(lockedTreasuryBalance);
    }

    function totalSupply() external view returns (uint256) {
        return circulatingSupply();
    }
}

interface ITokenUtils {
    function lockedSupply() external view returns (uint);
    function uniswapV2Pair() external view returns (address);
    function balanceOf(address owner) external view returns (uint);
    function totalSupply() external view returns (uint256);
}
