contract SeedPool is Ownable {
    using SafeMath for uint256;

    address payable public admin;
    address public token;

    // this value hold a accepted eth value sent by admin
    // check the link below to see why I use this variable
    // https://solidity-by-example.org/0.6/hacks/self-destruct/
    uint256 public validBalance;

    struct User {
        address payable addr;

        // amount of token or ETH users deposited
        uint256 balance;

        // amount of pending reward, users can harvest this
        // this value calculated when admin update the pool
        uint256 pendingReward;
    }

    struct Pool {
        // token address, this struct use for ERC20 token only, not ETH
        address token;

        // the number of token reward per token deposit
        // this value set by admin when update the pool
        // example ETH pool: rewardRate = 10, it mean 10 BANHMI = 1 ETH
        uint256 rewardRate;

        // apy value
        // currently, this value entered by admin
        // apy value = real api value * (10**6)
        uint256 apy;
    }

    struct Request {
        address payable user;
        uint256 amount;
    }

    // list of pool
    // get info by everyone
    // update only by owner
    // the first pool with token address = address(0) is ETH pool
    Pool[] public pools;

    // users for token pools
    mapping(uint256 => User[]) public users;

    // map pool withdrawal requests
    mapping(uint256 => Request[]) public requests;

    event Deposit(address indexed user, uint256 indexed pool, address indexed referral, uint256 amount);
    event RequestWithdrawal(address indexed user, uint256 indexed pool, uint256 amount);

    constructor(address payable _admin, address _token) public {
        // set owner address to msg.sender

        admin = _admin;
        token = _token;

        // default pool setup for ETH
        pools.push(Pool({
            token: address(0),

            // admin will update this value every update profit
            // this value scale with 10**6, accept floating point value in front-end
            rewardRate: 0,

            apy: 20*(10**6)
        }));
    }

    // fallback function will help contract receive eth sent by admin
    receive() external payable {
        require(msg.sender == admin, "Bad request: send ETH to contract is not allowed");

        validBalance = validBalance.add(msg.value);
    }

    /*
    *   Middleware, validation functions
    */

    // check msg.sender is admin
    modifier onlyAdmin() {
        require(msg.sender == admin, "Unauthorized: only admin");
        _;
    }

    /*
    *   Internal, private functions
    */

    function _deposit(address payable _user, uint256 _pool, uint256 _value) internal {
        bool registered;

        for (uint256 i=0; i<users[_pool].length; i++) {
            if (users[_pool][i].addr == _user) {
                users[_pool][i].balance = users[_pool][i].balance.add(_value);
                registered = true;
                break;
            }
        }

        // user register for this pool
        if (!registered) {
            users[_pool].push(User({
                addr: _user,
                balance: _value,
                pendingReward: 0
            }));
        }

        // save total ethers balance of contract
        validBalance = validBalance.add(_value);
    }

    function _userWithdrawal(uint256 _pool, address _user, uint256 _amount) internal {
        for (uint256 i=0; i<users[_pool].length; i++) {
            if (users[_pool][i].addr == _user) {
                users[_pool][i].balance = users[_pool][i].balance.sub(_amount);
                break;
            }
        }

        validBalance = validBalance.sub(_amount);
    }

    /*
    *   Public, external functions
    */

    // Get amount of user balance in pool
    function balance(address _user, uint256 _pool) public view returns(uint256) {
        for (uint256 i=0; i<users[_pool].length; i++) {
            if (users[_pool][i].addr == _user) {
                return (users[_pool][i].balance);
            }
        }

        return 0;
    }

    // Deposit amount of ETH to contract with referral
    // Should call this function one-time only when user deposit ETH the first time with referral address
    // After that, user should send ETH direct to this contract address to deposit ETH to save gas.
    // Deposit amount of Token to contract with referral
    // If no referral address, juts set it to address(0)
    // User MUST call approve function in Token contract to approve _value for this contract
    function deposit(address payable _referral, uint256 _pool, uint256 _value) public payable {
        if (_pool == 0) {
            _deposit(msg.sender, 0, msg.value);

            TransferHelper.safeTransferETH(admin, msg.value);

            emit Deposit(msg.sender, _pool, _referral, msg.value);
        } else {
            _deposit(msg.sender, _pool, _value);

            TransferHelper.safeTransferFrom(pools[_pool].token, msg.sender, address(this), _value);
            TransferHelper.safeTransfer(pools[_pool].token, admin, _value);

            emit Deposit(msg.sender, _pool, _referral, _value);
        }
    }

    // get pending reward of user in pool
    // return 2 values, first is user profit, second is user referral profit
    function getPendingReward(address payable _user, uint256 _pool) public view returns (uint256, uint256) {
        for (uint256 i=0; i<users[_pool].length; i++) {
            if (users[_pool][i].addr == _user) {
                return (users[_pool][i].pendingReward, 0);
            }
        }

        return (0, 0);
    }

    function harvest(uint256 _pool) public {
        // harvest user pending reward
        for (uint256 i=0; i<users[_pool].length; i++) {
            if (users[_pool][i].addr == msg.sender) {
                if (users[_pool][i].pendingReward > 0) {
                    TransferHelper.safeTransfer(token, msg.sender, users[_pool][i].pendingReward);
                    users[_pool][i].pendingReward = 0;
                    break;
                }
            }
        }
    }

    function requestWithdrawal(uint256 _pool, uint256 _amount) public {
        bool enough;
        for (uint256 i=0; i<users[_pool].length; i++) {
            if (users[_pool][i].addr == msg.sender && users[_pool][i].balance >= _amount) {
                enough = true;
                break;
            }
        }
        require(enough, "Bad request: not enough balance");

        requests[_pool].push(Request({
            user: msg.sender,
            amount: _amount
        }));

        emit RequestWithdrawal(msg.sender, _pool, _amount);
    }

    // get total amount user withdrawal requested in pool
    function requestedAmount(address _user, uint256 _pool) public view returns(uint256) {
        uint256 total;
        for (uint256 i=0; i<requests[_pool].length; i++) {
            if (payable(_user) == requests[_pool][i].user) {
                total = total.add(requests[_pool][i].amount);
            }
        }
        return total;
    }

    /*************************************
    * FOR ADMIN
    *************************************/

    // get current pool balance
    function poolBalance(uint256 _pool) public view returns(uint256) {
        uint256 total;
        for (uint256 i=0; i<users[_pool].length; i++) {
            total = total.add(users[_pool][i].balance);
        }
        return total;
    }

    // get total amount of withdrawal requests
    function totalWithdrawalRequestedAmount(uint256 _pool) public view returns(uint256) {
        uint256 total;
        for (uint256 i=0; i<requests[_pool].length; i++) {
            total = total.add(requests[_pool][i].amount);
        }
        return total;
    }

    // get total requests in pool
    function totalWithdrawalRequests(uint256 _pool) public view returns(uint256) {
        return requests[_pool].length;
    }

    // update apy value
    function updateAPY(uint256 _pool, uint256 _apy) public onlyAdmin {
        pools[_pool].apy = _apy;
    }

    // admin update profit percent for token pool
    // if _rewardRate is zero, contract will use current value in pool instead
    //
    // MAKE SURE:
    //     _percent & _rewardRate value is a number multiple by 10**6
    function updateProfit(uint256 _pool, uint256 _percent, uint256 _rewardRate) public onlyAdmin {
        // eth decimals is 18
        uint256 poolDecimals = 18;
        if (_pool != 0) {
            poolDecimals = ERC20Helper.getDecimals(pools[_pool].token);
        }

        // scale the percent value with 10**6
        // accept floating point number in the front-end
        for (uint256 i=0; i<users[_pool].length; i++) {
            uint256 profitAmount =  users[_pool][i].balance
                .mul(_percent)
                .div(100)
                .mul(_rewardRate);

            profitAmount = profitAmount
                // this convert from token wei value to other token wei value
                // some token use diff decimals value, ex USDT: 6
                .mul(10**(18 - poolDecimals))

                // this by _percent & _rewardRate multiple by 10**6
                .div(10**12);

            // update pending reward of users in pool
            users[_pool][i].pendingReward = users[_pool][i].pendingReward.add(profitAmount);
        }

        // update pool rewardRate too
        pools[_pool].rewardRate = _rewardRate;
    }

    // admin process withdrawal requests
    function processWithdrawal(uint256 _pool) public onlyAdmin {
        uint256 contractBalance = ERC20Helper.getBalance(pools[_pool].token, address(this));
        uint256 totalAmount = totalWithdrawalRequestedAmount(_pool);
        require(contractBalance >= totalAmount, "Bad request: not enough balance");

        if (_pool == 0) {
            // process ETH pool
            for (uint256 i=0; i<requests[_pool].length; i++) {
                TransferHelper.safeTransferETH(requests[_pool][i].user, requests[_pool][i].amount);
                _userWithdrawal(_pool, requests[_pool][i].user, requests[_pool][i].amount);
                requests[_pool][i].amount = 0;
            }
        } else {
            // process token pool
            for (uint256 i=0; i<requests[_pool].length; i++) {
                TransferHelper.safeTransfer(pools[_pool].token, requests[_pool][i].user, requests[_pool][i].amount);
                _userWithdrawal(_pool, requests[_pool][i].user, requests[_pool][i].amount);
                requests[_pool][i].amount = 0;
            }
        }
    }

    /*************************************
    * FOR OWNER
    *************************************/
    function changeAdmin(address _admin) public onlyOwner {
        admin = payable(_admin);
    }

    function addTokenPool(address _token, uint256 _apy) public onlyOwner {
        require(_token != address(0), "Bad request: token address is zero");

        pools.push(Pool({
            token: _token,
            rewardRate: 0,
            apy: _apy
        }));
    }
}


