contract IPhoneFactory is Ownable {
    using SafeMath for uint256;
    uint256 public _currentTokenId;
    uint8 public IPHONE_SUCCESS = 1;
    uint8 public IPHONE_FAILED = 0;
    uint8 public IPHONE_MADE_MAX = 3;
    uint8 public successIphoneMade = 0;
    uint8 public createdIphoneMade = 0;
    address public AppleInc;

    uint256[] public _IPhonesMade;
    mapping(address => bool) public _iCompanies;

    modifier requireManufactory {
        require(_iCompanies[msg.sender]);
        _;
    }

    modifier onlyAppleInc {
        require(msg.sender == AppleInc);
        _;
    }

    constructor() public {
        AppleInc = msg.sender;
    }

    struct IProp {
        uint256 iType;
        uint256 iData;
    }

    event CreatePropIphone(uint256 indexed tokenId, IProp prop);

    mapping(uint256 => IProp) public _iProps;

    function random() public view returns (uint256) {
        uint256 randomnumber = uint256(
            keccak256(abi.encodePacked(now, msg.sender, _currentTokenId))
        ) % 999;
        randomnumber = randomnumber.add(1);
        return randomnumber;
    }

    function create(uint256 tokenId, uint256 _type) internal {
        _create(tokenId, _type, random());
    }

    function _create(
        uint256 tokenId,
        uint256 _type,
        uint256 _data
    ) internal {
        IProp memory iProp = IProp({iType: _type, iData: _data});
        _iProps[tokenId] = iProp;
        emit CreatePropIphone(tokenId, iProp);
    }

    function setAppleInc(address _newAppleInc) public onlyOwner {
        AppleInc = _newAppleInc;
    }

    function addManufactory(address _newManufactory) public onlyAppleInc {
        _iCompanies[_newManufactory] = true;
    }

    function removeManufactory(address _company) public onlyAppleInc {
        require(_iCompanies[_company]);
        _iCompanies[_company] = false;
    }

    function assembleYourIPhone(
        uint256 tokenChip,
        uint256 tokenRom,
        uint256 tokenColor
    ) public virtual {}
}

