    uint256 GRAPHITE = 666; // Graphite Iphone
    uint256 SILVER = 777; // Silver Iphone
    uint256 GOLD = 888; // Gold Iphone
    uint256 PACIFIC_BLUE = 999; // Pacific Blue Iphone
    uint256[] COLORS = [
        WHITE,
        BLACK,
        BLUE,
        GREEN,
        RED,
        GRAPHITE,
        SILVER,
        GOLD,
        PACIFIC_BLUE
    ];

