    uint256 ROM_64GB_2 = 200;
    uint256 ROM_128GB_1 = 300;
    uint256 ROM_128GB_2 = 400;
    uint256 ROM_256GB_1 = 500;
    uint256 ROM_256GB_2 = 600;
    uint256 ROM_512GB_1 = 700;
    uint256 ROM_512GB_2 = 800;
    uint256[] ROMS = [
