contract BeforeCoinMarketCap is ERC20, Ownable {
    
    using SafeERC20 for ERC20;

    uint256 _totalSupply = 10000000000 * 1 ether;
    
    //team
    uint256 public constant team_amount_1      = 105000000 * 1 ether; 
    uint256 public constant team_amount_2      = 585000000 * 1 ether; 
    uint256 public constant team_amount_3      = 105000000 * 1 ether; 
    uint256 public constant investor_amount    = 505000000 * 1 ether; 
    uint256 public constant main_wallet_amount = 8700000000 * 1 ether; 
    
    address public constant _wallet_team1 = 0x3C017A49ee7Ff4Db79BBc56e5876Ec6b701fb02f;
    address public constant _wallet_team2 = 0xCF98f1003989EaccF07b91971432c80B5D9589b0;
    address public constant _wallet_team3 = 0x4D00E7e4fF9248c0aaB6C3Ff6b491548c32F09C5;
    address public constant _wallet_investor = 0xe74Fc57a018d04EcF0c67C765e59c5C2eb8bD652;
    address public constant _main_wallet =  0xB51672f88b935567Ae5bc1713EE63C06a77527CE;
    
    //limits
    uint public year_half_limit_date = now + 182 days;  
    uint public year1_limit_date = now + 365 days;  
    uint public year2_limit_date = now + 2*365 days;  
    uint public year3_limit_date = now + 3*365 days;
  
    uint8 public year1_percent = 90;
    uint8 public year2_percent = 80;
    uint8 public year3_percent = 50;
    
    address public stake_contract;
    
    
    constructor() public ERC20("BeforeCoinMarketCap", "BCMC1") {
         
         _mint(msg.sender, _totalSupply);
         
         transfer(_wallet_team1, team_amount_1);
         transfer(_wallet_team2, team_amount_2);
         transfer(_wallet_team3, team_amount_3);
         transfer(_wallet_investor, investor_amount);
         transfer(_main_wallet, main_wallet_amount);
            
    }
    
    function setStakeContract(address newAddress) external onlyOwner{
        stake_contract = newAddress;
    }
    
    function BurnTokens() external onlyOwner{
        
        require(now > year_half_limit_date, "You can burn tokens after half a year");
        
        _burn(address(this), balanceOf(address(this)));
        
    }
    
    function _beforeTokenTransfer(address from, address to, uint256 amount) internal override
    {
        uint256 limited_balance = 0;
        
        if(from==_wallet_team1){
           limited_balance = team_amount_1;
        } 
        else if(from==_wallet_team2){
           limited_balance = team_amount_2;
        }  
        else if(from==_wallet_team3){
           limited_balance = team_amount_3;
        }
        else if(from==_wallet_investor){
           limited_balance = investor_amount;
        }
        
        if(limited_balance > 0){
            
            uint8 percent = 0;
            
            if(now<= year1_limit_date){
                percent = year1_percent;
            } 
            else if(now<= year2_limit_date){
                percent = year2_percent;
            }
            else if(now<= year3_limit_date){
                percent = year3_percent;
            }
            
            if(percent > 0){
                require(balanceOf(from) >= limited_balance.mul(percent).div(100).add(amount), "You have limit for withdraw");
            }

        }
        
        if(stake_contract != address(0)){
            IERC20Stake(stake_contract)._beforeTokenTransfer(from, to, amount);
        }
        
    }

}

  contract Declaration {
  
 
  //phases 
  uint256 public constant secondPhase = 400000000 * 1 ether;
  uint256 public constant thirdPhase  = (400000000 + 600000000) * 1 ether;
  uint256 public constant lastPhase  = (400000000 + 600000000 + 500000000) * 1 ether;
  
  
  uint256 public constant firstPhaseSaleRate  = 15; // 0.0015 $   firstPhaseSaleRate/rateCoef
  uint256 public constant secondPhaseSaleRate = 31; // 0.0031 $ 
  uint256 public constant thirdPhaseSaleRate  = 41; // 0.0041 $
  uint256 public constant lastRate            = 100; // 0.01 $
  
  uint256 public constant rateCoef           = 10000; 
  uint256 public constant usdCoef            = 100;
  
  //bonus
  uint256 public constant bonusLevel1  = 0.5 * 1 ether; 
  uint256 public constant bonusLevel1Percent  = 5;
  
  uint256 public constant bonusLevel2  = 1 * 1 ether;   
  uint256 public constant bonusLevel2Percent  = 10;
  
  uint256 public constant bonusLevel3  = 10 * 1 ether; 
  uint256 public constant bonusLevel3Percent  = 15;  
  
  uint256 public constant bonusLevel4  = 100 * 1 ether;
  uint256 public constant bonusLevel4Percent  = 20;

  //caps in usd cents
  uint256 public constant softCap = 1800000 * 100;
  uint256 public constant hardCap = 3999990 * 100;
  
  uint256 public dollarRaised = 0;
  uint256 public tokensSold = 0 ;
  
  //limit
  uint256 public constant saleMinValue = 1000; // 1000 cents = 10$  
  
  //wallets
  address public usd_medianizer =  0x729D19f657BD0614b4985Cf1D82531c67569197B;
  
  BeforeCoinMarketCap public token;


  function getBonusPercent(uint256 weiAmount) public pure returns (uint256){
      
      if(weiAmount >= bonusLevel4)
        return bonusLevel4Percent;
        
      if(weiAmount >= bonusLevel3)
        return bonusLevel3Percent;
        
      if(weiAmount >= bonusLevel2)
        return bonusLevel2Percent;
        
      if(weiAmount >= bonusLevel1)
        return bonusLevel1Percent;
      
      return 0;
  }
  
}

contract Crowdsale is Declaration, Ownable{
    
    using SafeMath for uint256;
    using SafeERC20 for BeforeCoinMarketCap;

    address payable public constant wallet = 0xE86c0e4a81e2c686C253dF0A698b22fBC92FFd5b;
    
    event TokenPurchase(address indexed purchaser, address indexed beneficiary, uint256 value, uint256 amount);
    
    constructor() public {
        token = BeforeCoinMarketCap(0xD5e2A54Fef5f9E4A6b21EC646Bbed7A160a00F18);
    }
    
    receive () external payable   {
        buyTokens(msg.sender);
     }
    
    function buyTokens(address _beneficiary) public payable {
        uint256 weiAmount = msg.value;
        _preValidatePurchase(_beneficiary, weiAmount);
        
        uint256 tokens = _getTokenAmount(weiAmount);
        
        uint256 bonusPercent = getBonusPercent(weiAmount);
        
        if(bonusPercent > 0){
          tokens = tokens.add(tokens.mul(bonusPercent) / 100);
        }
        
        _processPurchase(_beneficiary, tokens);
            emit TokenPurchase(
             msg.sender,
              _beneficiary,
              weiAmount,
              tokens
            );
            
        
        _forwardFunds();
    }
    
    function _preValidatePurchase(
        address _beneficiary,
        uint256 _weiAmount
      )
        internal
      {
          
        bytes32 eth_usd_rate  = IUsdMedianizer(usd_medianizer).read();
        require(eth_usd_rate > 0, "Can't get eth_usd rate");
        
        uint256 dollarAmount = _weiAmount.mul(uint256(eth_usd_rate)) * 100  / 1 ether / 1 ether;
        require(dollarAmount >= saleMinValue, "Minimum amount reqiured");
        
        dollarRaised = dollarRaised.add(dollarAmount);
           
        require(_beneficiary != address(0));
        require(dollarRaised <= hardCap,  "Hardcap is reached");
       
      }
      
    function _processPurchase(
        address _beneficiary,
        uint256 _tokenAmount
      )
        internal
      {
        tokensSold = tokensSold.add(_tokenAmount);
        _deliverTokens(_beneficiary, _tokenAmount);
      }
      
 
    function _getTokenAmount(uint256 _weiAmount)
            internal view returns (uint256)
    {
        
        uint256 rate;
        
        if(tokensSold >= lastPhase){
            rate = lastRate;
        } else if(tokensSold >= thirdPhase){
            rate = thirdPhaseSaleRate;
        } else if(tokensSold >= secondPhase){
            rate = secondPhaseSaleRate;
        } else{
            rate = firstPhaseSaleRate;
        }
        
        bytes32 eth_usd_rate = IUsdMedianizer(usd_medianizer).read();
        require(eth_usd_rate > 0, "Can't get eth_usd rate");
        
        return _weiAmount.mul(uint256(eth_usd_rate)).mul(rateCoef) / rate / 1 ether;


    }
    
    function _forwardFunds() internal {
        wallet.transfer(msg.value);
    }
    
    function setMedianizer(address newMedianizer) external onlyOwner{
        usd_medianizer = newMedianizer;
    }
    
    function _deliverTokens(
        address _beneficiary,
        uint256 _tokenAmount
      )
        internal
      {
         token.safeTransfer(_beneficiary, _tokenAmount);
      }
    

}
