  "sources": {
    "contracts/0.7.x/Linguo.sol": {
      "content": "/**
 * @authors: [@unknownunknown1, @hbarcelos]
 * @reviewers: [@fnanni-0, @nix1g]
 * @auditors: []
 * @bounties: []
 * @deployments: []
