        _setRoleAdmin(WHITELIST_ADMIN, WHITELIST_ADMIN);
        _setRoleAdmin(WHITELISTED, WHITELIST_ADMIN);
        _setupRole(WHITELIST_ADMIN, msg.sender);
    }

