// computes square roots using the babylonian method
// https://en.wikipedia.org/wiki/Methods_of_computing_square_roots#Babylonian_method
library Babylonian {
    function sqrt(uint y) internal pure returns (uint z) {
        if (y > 3) {
            z = y;
            uint x = y / 2 + 1;
            while (x < z) {
                z = x;
                x = (y / x + x) / 2;
            }
        } else if (y != 0) {
            z = 1;
        }
        // else z = 0
    }
}

library FixedPoint {
    // range: [0, 2**112 - 1]
    // resolution: 1 / 2**112
    struct uq112x112 {
        uint224 _x;
    }

    // range: [0, 2**144 - 1]
    // resolution: 1 / 2**112
    struct uq144x112 {
        uint _x;
    }

    uint8 private constant RESOLUTION = 112;
    uint private constant Q112 = uint(1) << RESOLUTION;
    uint private constant Q224 = Q112 << RESOLUTION;

    // encode a uint112 as a UQ112x112
    function encode(uint112 x) internal pure returns (uq112x112 memory) {
        return uq112x112(uint224(x) << RESOLUTION);
    }

    // encodes a uint144 as a UQ144x112
    function encode144(uint144 x) internal pure returns (uq144x112 memory) {
        return uq144x112(uint256(x) << RESOLUTION);
    }

    // divide a UQ112x112 by a uint112, returning a UQ112x112
    function div(uq112x112 memory self, uint112 x) internal pure returns (uq112x112 memory) {
        require(x != 0, 'FixedPoint: DIV_BY_ZERO');
        return uq112x112(self._x / uint224(x));
    }

    // multiply a UQ112x112 by a uint, returning a UQ144x112
    // reverts on overflow
    function mul(uq112x112 memory self, uint y) internal pure returns (uq144x112 memory) {
        uint z;
        require(y == 0 || (z = uint(self._x) * y) / y == uint(self._x), "FixedPoint: MULTIPLICATION_OVERFLOW");
        return uq144x112(z);
    }

    // returns a UQ112x112 which represents the ratio of the numerator to the denominator
    // equivalent to encode(numerator).div(denominator)
    function fraction(uint112 numerator, uint112 denominator) internal pure returns (uq112x112 memory) {
        require(denominator > 0, "FixedPoint: DIV_BY_ZERO");
        return uq112x112((uint224(numerator) << RESOLUTION) / denominator);
    }

    // decode a UQ112x112 into a uint112 by truncating after the radix point
    function decode(uq112x112 memory self) internal pure returns (uint112) {
        return uint112(self._x >> RESOLUTION);
    }

    // decode a UQ144x112 into a uint144 by truncating after the radix point
    function decode144(uq144x112 memory self) internal pure returns (uint144) {
        return uint144(self._x >> RESOLUTION);
    }

    // take the reciprocal of a UQ112x112
    function reciprocal(uq112x112 memory self) internal pure returns (uq112x112 memory) {
        require(self._x != 0, 'FixedPoint: ZERO_RECIPROCAL');
        return uq112x112(uint224(Q224 / self._x));
    }

    // square root of a UQ112x112
    function sqrt(uq112x112 memory self) internal pure returns (uq112x112 memory) {
        return uq112x112(uint224(Babylonian.sqrt(uint256(self._x)) << 56));
    }
}

library UniswapV2OracleLibrary {
    using FixedPoint for *;

    // helper function that returns the current block timestamp within the range of uint32, i.e. [0, 2**32 - 1]
    function currentBlockTimestamp() internal view returns (uint32) {
        return uint32(block.timestamp % 2 ** 32);
    }

    // produces the cumulative price using counterfactuals to save gas and avoid a call to sync.
    function currentCumulativePrices(
        address pair
    ) internal view returns (uint price0Cumulative, uint price1Cumulative, uint32 blockTimestamp) {
        blockTimestamp = currentBlockTimestamp();
        price0Cumulative = IUniswapV2Pair(pair).price0CumulativeLast();
        price1Cumulative = IUniswapV2Pair(pair).price1CumulativeLast();

        // if time has elapsed since the last update on the pair, mock the accumulated price values
        (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast) = IUniswapV2Pair(pair).getReserves();
        if (blockTimestampLast != blockTimestamp) {
            // subtraction overflow is desired
            uint32 timeElapsed = blockTimestamp - blockTimestampLast;
            // addition overflow is desired
            // counterfactual
            price0Cumulative += uint(FixedPoint.fraction(reserve1, reserve0)._x) * timeElapsed;
            // counterfactual
            price1Cumulative += uint(FixedPoint.fraction(reserve0, reserve1)._x) * timeElapsed;
        }
    }
}

interface IUniswapV2Pair {
    function sync() external;
    function token0() external view returns (address);
    function token1() external view returns (address);
    function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
    function price0CumulativeLast() external view returns (uint);
    function price1CumulativeLast() external view returns (uint);
}

contract UniswapOracle {

  using FixedPoint for *;
  using SafeMath for uint;

  struct PriceData {
    address token0;
    address token1;

    uint    price0CumulativeLast;
    uint    price1CumulativeLast;
    uint32  blockTimestampLast;
    uint    nextUpdateAt;

    uint price0Average;
    uint price1Average;
  }

  mapping (address => PriceData) private pairs;

  uint public constant PERIOD = 2 hours;
  address public updater;

  modifier onlyUpdater() {
    require(msg.sender == updater, "UniswapOracle: caller is not the updater");
    _;
  }

  function initialize() public {
    require(updater == address(0), "UniswapOracle: already initialized");
    updater = msg.sender;
  }

  function addPair(address _pair, uint _startTime) public onlyUpdater {
    PriceData storage priceData = pairs[_pair];
    require(priceData.token0 == address(0) && priceData.token1 == address(0), "UniswapOracle: pair already added");

    IUniswapV2Pair uniswapPair = IUniswapV2Pair(_pair);

    uint reserve0;
    uint reserve1;
    (reserve0, reserve1, priceData.blockTimestampLast) = uniswapPair.getReserves();
    require(reserve0 != 0 && reserve1 != 0, 'UniswapOracle: no reserves');

    priceData.token0 = uniswapPair.token0();
    priceData.token1 = uniswapPair.token1();
    priceData.price0CumulativeLast = uniswapPair.price0CumulativeLast();
    priceData.price1CumulativeLast = uniswapPair.price1CumulativeLast();
    priceData.price0Average = reserve1.mul(1e18).div(reserve0);
    priceData.price1Average = reserve0.mul(1e18).div(reserve1);
    priceData.nextUpdateAt = _startTime;
  }

  function update(address _pair) public onlyUpdater {
    pairs[_pair] = priceCurrent(_pair);
    require(pairs[_pair].token0 != address(0), "UniswapOracle: pair not supported");
    require(isUpdateRequired(_pair), "UniswapOracle: too early");
    
    uint cyclesElapsed = 1 + (block.timestamp.sub(pairs[_pair].nextUpdateAt)).div(PERIOD);
    pairs[_pair].nextUpdateAt += PERIOD.mul(cyclesElapsed);
  }

  function isUpdateRequired(address _pair) public view returns(bool) {
    return block.timestamp > pairs[_pair].nextUpdateAt;
  }

  function price0Last(address _pair) public view returns (uint amountOut) {
    return pairs[_pair].price0Average;
  }

  function price1Last(address _pair) public view returns (uint amountOut) {
    return pairs[_pair].price1Average;
  }

  function price0Current(address _pair) public view returns (uint amountOut) {
    return priceCurrent(_pair).price0Average;
  }

  function price1Current(address _pair) public view returns (uint amountOut) {
    return priceCurrent(_pair).price1Average;
  }

  function blockTimestampLast(address _pair) external view returns(uint32) {
    return pairs[_pair].blockTimestampLast;
  }

  function nextUpdateAt(address _pair) external view returns(uint) {
    return pairs[_pair].nextUpdateAt;
  }

  function price0CumulativeLast(address _pair) external view returns(uint) {
    return pairs[_pair].price0CumulativeLast;
  }

  function price1CumulativeLast(address _pair) external view returns(uint) {
    return pairs[_pair].price1CumulativeLast;
  }

  function priceCurrent(address _pair) internal view returns(PriceData memory) {
    PriceData memory priceData = pairs[_pair];

    (uint price0Cumulative, uint price1Cumulative, uint32 blockTimestamp) = UniswapV2OracleLibrary.currentCumulativePrices(address(_pair));
    uint32 timeElapsed = blockTimestamp - priceData.blockTimestampLast; // overflow is desired

    priceData.price0Average = uint(
      FixedPoint.uq112x112(
        uint224((price0Cumulative - priceData.price0CumulativeLast) / timeElapsed)
      ).mul(1e18).decode144()
    );

    priceData.price1Average = uint(
      FixedPoint.uq112x112(
        uint224((price1Cumulative - priceData.price1CumulativeLast) / timeElapsed)
      ).mul(1e18).decode144()
    );

    priceData.price0CumulativeLast = price0Cumulative;
    priceData.price1CumulativeLast = price1Cumulative;
    priceData.blockTimestampLast = blockTimestamp;

    return priceData;
  }
}
