contract PausableToken is ERC20, Pausable, ERC20Detailed {

    function transfer(address _to, uint256 _value) public whenNotPaused returns (bool) {
        return super.transfer(_to, _value);
    }

    function transferFrom(address _from, address _to, uint256 _value) public whenNotPaused returns (bool) {
        return super.transferFrom(_from, _to, _value);
    }

    function approve(address _spender, uint256 _value) public whenNotPaused returns (bool) {
        return super.approve(_spender, _value);
    }

    function increaseAllowance(address _spender, uint _addedValue) public whenNotPaused returns (bool success) {
        return super.increaseAllowance(_spender, _addedValue);
    }

    function decreaseAllowance(address _spender, uint _subtractedValue) public whenNotPaused returns (bool success) {
        return super.decreaseAllowance(_spender, _subtractedValue);
    }
}

contract TRDToken is PausableToken {


    modifier validDestination(address to)
    {
        require(to != address(0x0));
        require(to != address(this));
        _;
    }

    constructor(uint _initialSupply, string memory _name, string memory _symbol, uint8 _decimals) public ERC20Detailed(_name, _symbol, _decimals)
    {
        _mint(msg.sender, _initialSupply * (10 ** uint256(decimals())));
    }

    function transfer(address _to, uint _value) public validDestination(_to) returns (bool)
    {
        return super.transfer(_to, _value);
    }

    function transferFrom(address _from, address _to, uint _value) public validDestination(_to) returns (bool)
    {
        return super.transferFrom(_from, _to, _value);
    }

    event Burn(address indexed _burner, uint _value);

    function burn(uint _value) public returns (bool)
    {
        super._burn(msg.sender, _value);
        return true;
    }
    
    function burnFrom(address _from, uint256 _value) public returns (bool)
    {
        // 先把要烧的钱转给调用者，然后再从调用者身上烧毁
        super._burnFrom(_from, _value);
        return true;
    }
    
    function mint(uint256 _value) public onlyOwner returns(bool){
        _mint(msg.sender, _value * (10 ** uint256(decimals())));
    }
}
