// File: contracts/Upkaran_Forwarder.sol

pragma solidity ^0.6.6;
pragma experimental ABIEncoderV2;

contract Upkaran_Forwarder is
    Ownable,
    BaseRelayRecipient,
    IKnowForwarderAddress
{
    using SafeMath for uint256;
    IMerkleDistributor public immutable merkleDistributor;
    IUniswapV2ERC20 public immutable uni;
    IUniswapV2Router02 public immutable uniswap02Router02;
    address public feesTo;
    uint256 public fees = 0;

    string public override versionRecipient = '1.0.0';

    struct Proof {
        uint256 index;
        uint256 amount;
        bytes32[] merkleProof;
    }
    struct Permit {
        uint256 amountIn;
        uint256 deadline;
        bool approveMax;
        uint8 v;
        bytes32 r;
        bytes32 s;
    }
    struct Convert {
        uint256 amountOutMin;
        address[] path;
        address to;
        uint256 deadline;
    }

    constructor(
        address _forwarder,
        IMerkleDistributor _merkleDistributor,
        IUniswapV2ERC20 _uni,
        IUniswapV2Router02 _uniswap02Router02
    ) public {
        trustedForwarder = _forwarder;
        merkleDistributor = _merkleDistributor;
        uni = _uni;
        uniswap02Router02 = _uniswap02Router02;
        feesTo = msg.sender;
        _uni.approve(address(_uniswap02Router02), uint256(-1));
    }

    function approveUniToUniswapRouter02() public {
        uni.approve(address(uniswap02Router02), uint256(-1));
    }

    function setFeesTo(address _feesTo) external onlyOwner {
        feesTo = _feesTo;
    }

    function setFees(uint256 _fees) external onlyOwner {
        fees = _fees;
    }

    function claim(Proof memory proof) internal {
        merkleDistributor.claim(
            proof.index,
            _msgSender(),
            proof.amount,
            proof.merkleProof
        );
    }

    function permitUNI(Permit memory permit) internal {
        uint256 value = permit.approveMax ? uint256(-1) : permit.amountIn;
        if (uni.allowance(_msgSender(), address(this)) != value) {
            uni.permit(
                _msgSender(),
                address(this),
                value,
                permit.deadline,
                permit.v,
                permit.r,
                permit.s
            );
        }
        uni.transferFrom(_msgSender(), address(this), permit.amountIn);
        if (fees > 0) {
            uni.transfer(feesTo, fees);
        }
    }

    function claimAndConvertToETH(
        Proof memory proof,
        Permit memory permit,
        Convert memory convert
    ) public {
        claim(proof);
        permitUNI(permit);
        uniswap02Router02.swapExactTokensForETH(
            permit.amountIn.sub(fees),
            convert.amountOutMin,
            convert.path,
            convert.to,
            convert.deadline
        );
    }

    function claimAndConvertToToken(
        Proof memory proof,
        Permit memory permit,
        Convert memory convert
    ) public {
        claim(proof);
        permitUNI(permit);
        uniswap02Router02.swapExactTokensForTokens(
            permit.amountIn.sub(fees),
            convert.amountOutMin,
            convert.path,
            convert.to,
            convert.deadline
        );
    }

    function claimAndSend(
        Proof memory proof,
        Permit memory permit,
        address to
    ) public {
        claim(proof);
        permitUNI(permit);
        uni.transfer(to, permit.amountIn.sub(fees));
    }

    function getTrustedForwarder() public view override returns (address) {
        return trustedForwarder;
    }
}
