    "contracts/VaultProxy.sol": {
      "content": "pragma solidity ^0.6.0;


import "./StorageState.sol";

contract VaultProxy is StorageState {
  bytes32 private constant implementationPosition = bytes32(uint256(keccak256("ygy.proxy.implementation")) - 1);
  bytes32 private constant proxyOwnerPosition = bytes32(uint256(keccak256("ygy.proxy.owner")) - 1);

  constructor() public {
    _setUpgradeabilityOwner(msg.sender);
  }

  function setup(address _implementation, YGYStorageV1 __storage) external {
    require(msg.sender == proxyOwner());
    _setImplementation(_implementation, __storage);
  }

  function setupOwner(address _newOwner) external {
    require(msg.sender == proxyOwner());
    _setUpgradeabilityOwner(_newOwner);
  }

  // setter to set the position of an implementation from the implementation position onwards
  function _setImplementation(address _newImplementation, YGYStorageV1 __storage) internal {
    require(msg.sender == proxyOwner());
    _storage = __storage;
    bytes32 position = implementationPosition;

    assembly {
      sstore(position, _newImplementation)
    }
  }

  // retrieving the address at the implementation position
  function implementation() public view returns (address impl) {
    bytes32 position = implementationPosition;
    assembly {
      impl := sload(position)
    }
  }

  function proxyOwner() public view returns (address owner) {
    bytes32 position = proxyOwnerPosition;
    assembly {
      owner := sload(position)
    }
  }

  function _setUpgradeabilityOwner(address _newProxyOwner) private {
    bytes32 position = proxyOwnerPosition;
    assembly {
      sstore(position, _newProxyOwner)
    }
  }

  fallback() external payable {
    address addr = implementation();

    assembly {
      calldatacopy(0, 0, calldatasize())
      let result := delegatecall(gas(), addr, 0, calldatasize(), 0, 0)
      returndatacopy(0, 0, returndatasize())
      switch result
        case 0 {
          revert(0, returndatasize())
        }
        default {
          return(0, returndatasize())
        }
    }
  }
}
"
