    "contracts/libraries/UserHelper.sol": {
      "content": "pragma solidity ^0.6.0;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts-ethereum-package/contracts/math/SafeMath.sol";
import "../StorageState.sol";

library UserHelper {
  using SafeMath for uint256;

  function effectiveAmount(YGYStorageV1.UserInfo memory self) internal pure returns (uint256) {
    return self.amount.add(self.boostAmount);
  }

  function getUser(
    uint256 _poolId,
    address _user,
    YGYStorageV1 _storage
  ) internal view returns (YGYStorageV1.UserInfo memory) {
    (uint256 amount, uint256 rewardDebt, uint256 rewardDebtYGY, uint256 boostAmount, uint256 boostLevel, uint256 spentMultiplierTokens) =
      _storage.userInfo(_poolId, _user);

    return
      YGYStorageV1.UserInfo({
        amount: amount,
        rewardDebt: rewardDebt,
        rewardDebtYGY: rewardDebtYGY,
        boostAmount: boostAmount,
        boostLevel: boostLevel,
        spentMultiplierTokens: spentMultiplierTokens
      });
  }

  function userRewards(
    YGYStorageV1.UserInfo memory self,
    uint256 _poolId,
    YGYStorageV1 _storage
  ) internal view returns (uint256 RAMRewards, uint256 YGYRewards) {
    (, , uint256 accRAMPerShare, uint256 accYGYPerShare, , ) = YGYStorageV1(_storage).getPoolInfo(_poolId);
    return (
      effectiveAmount(self).mul(accRAMPerShare).div(1e12).sub(self.rewardDebt),
      effectiveAmount(self).mul(accYGYPerShare).div(1e12).sub(self.rewardDebtYGY)
    );
  }

  // Returns the multiplier for user.
  function getTotalMultiplier(
    YGYStorageV1.UserInfo memory self,
    uint256 _level,
    address _user,
    YGYStorageV1 _storage
  ) internal view returns (uint256) {
    uint256 NFTBoost = _storage.getNFTBoost(_user);
    return _storage.getBoostLevelMultiplier(_level).add(NFTBoost);
  }

  function updateDebts(YGYStorageV1.UserInfo memory self, YGYStorageV1.PoolInfo memory _pool) internal pure {
    self.rewardDebt = effectiveAmount(self).mul(_pool.accRAMPerShare).div(1e12);
    self.rewardDebtYGY = effectiveAmount(self).mul(_pool.accYGYPerShare).div(1e12);
  }

  function adjustEffectiveStake(
    YGYStorageV1.UserInfo memory self,
    YGYStorageV1.PoolInfo memory _pool,
    address _user,
    uint256 _newLevel,
    bool _isWithdraw,
    YGYStorageV1 _storage
  ) internal view {
    uint256 prevBalancesAccounting = self.boostAmount;
    // Calculate and set self's new accounting balance
    uint256 accTotalMultiplier = getTotalMultiplier(self, _newLevel > 0 ? _newLevel : self.boostLevel, _user, _storage);

    uint256 newBalancesAccounting = self.amount.mul(accTotalMultiplier).div(100);
    self.boostAmount = newBalancesAccounting;

    // Adjust total accounting supply accordingly
    if (_isWithdraw) {
      _pool.effectiveAdditionalTokensFromBoosts = _pool.effectiveAdditionalTokensFromBoosts.sub(
        prevBalancesAccounting.sub(newBalancesAccounting)
      );
    } else {
      if (prevBalancesAccounting > newBalancesAccounting) {
        _pool.effectiveAdditionalTokensFromBoosts = _pool.effectiveAdditionalTokensFromBoosts.sub(prevBalancesAccounting).add(
          newBalancesAccounting
        );
      } else {
        _pool.effectiveAdditionalTokensFromBoosts = _pool.effectiveAdditionalTokensFromBoosts.add(newBalancesAccounting).sub(
          prevBalancesAccounting
        );
      }
    }
  }
}
"
    },
    "contracts/NFTFactory.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.6.12;
pragma experimental ABIEncoderV2;

import "./interfaces/INFT.sol";
import "./interfaces/IRAMVault.sol";
import "./NFT.sol";

import "@openzeppelin/contracts-ethereum-package/contracts/access/Ownable.sol";
import "./StorageState.sol";

contract NFTFactory is StorageState, OwnableUpgradeSafe {
  address[] public contracts;
  address public bondedContract;
  IRAMVault ramVault;
  // address public lastContractAddress;
  mapping(address => bool) public inUse;

  event NFTMinted(string tokenName, address to, uint256 tokenId);
  event NFTBurned(string tokenName, address from, uint256 tokenId);

  constructor(YGYStorageV1 __storage) public {
    __Ownable_init();
    _storage = __storage;
  }

  function deployNFT(
    string memory name,
    string memory symbol,
    string memory tokenURI,
    uint256 contractId,
    uint256 propertyChoices,
    address admin,
    bool allowTrade,
    bool isCapped,
    uint256 capAmount,
    address _ramVault
  ) public returns (NFT newContract) {
    require(_msgSender() == owner() || _msgSender() == bondedContract, "Invalid caller: can't deploy NFT");
    ramVault = IRAMVault(_ramVault);

    // Deploy new NFT
    NFT nft = new NFT(name, symbol, tokenURI, contractId, propertyChoices, admin, allowTrade, isCapped, capAmount, _ramVault);

    address addressNFT = address(nft);

    // Add to owned NFTs
    contracts.push(addressNFT);

    return nft;
  }

  function balanceOf(INFT _nft, address _who) external view returns (uint256) {
    return _nft.balanceOf(_who);
  }

  function isOwner(
    INFT _nft,
    address _who,
    uint256 _tokenId
  ) external view returns (bool) {
    return _nft.ownerOf(_tokenId) == _who;
  }

  function mint(
    INFT _nft,
    address _to,
    uint256 _randomness
  ) external returns (uint256) {
    require(_msgSender() == bondedContract || _msgSender() == owner());
    uint256 tokenId = _nft.mint(_to, _randomness, _storage);

    emit NFTMinted(_nft.name(), _to, tokenId);
    return tokenId;
  }

  function burn(INFT _nft, uint256 _tokenId) external {
    require(_nft.ownerOf(_tokenId) == _msgSender() || _msgSender() == bondedContract);
    _nft.burn(_tokenId);
    emit NFTBurned(_nft.name(), _msgSender(), _tokenId);
  }

  function setNFTProperties(address _nft, YGYStorageV1.NFTProperty[] memory _properties) external {
    require(msg.sender == owner());
    _storage.setNFTPropertiesForContract(_nft, _properties);
  }

  function useNFT(
    INFT _nft,
    uint256 _tokenId,
    uint256 _poolId
  ) public {
    require(_nft.ownerOf(_tokenId) == msg.sender, "User not owner");
    YGYStorageV1.NFTUsage[] memory nftUsage = _storage.getNFTsInUse(msg.sender);

    uint256 contractId = _nft.contractId();
    bool alreadyInUse;

    // check if nft instance is already in use for the user
    for (uint256 i; i < nftUsage.length; i++) {
      if (nftUsage[i].contractId == contractId) {

        alreadyInUse = true;
        break;
      }
    }
    require(!alreadyInUse, "NFT already in use");

    _nft.transferFrom(msg.sender, address(ramVault), _tokenId);
    ramVault.NFTUsage(msg.sender, address(_nft), _tokenId, _poolId);
  }

  function setNFTUsage(address _nft, bool _inUse) external onlyOwner {
    inUse[_nft] = _inUse;
  }

  function bondContract(address _addr) external returns (bool) {
    require(msg.sender == owner());
    bondedContract = _addr;
    return true;
  }

  function getContractCount() external view returns (uint256 contractCount) {
    return contracts.length;
  }
}
"
