contract Vesting is Ownable {

  using SafeMath for uint256;

  // start of token distribution
  uint256 public immutable start;
  // duration of token distribution
  uint256 public immutable duration;
  IERC20 private immutable _token;

  mapping (address => uint256) public released;
  mapping (address => uint256) public vested;
  uint256 private _totalVested;
  uint256 private _totalReleased;

  constructor(uint256 _start, uint256 _duration, IERC20 token) public {
    _token = token;
    start = _start;
    duration = _duration;
  }

  function release(address to) external {
    uint256 unreleased = releasableAmount(to);
    require(unreleased > 0, "TeamVesting: no tokens are due");

    released[to] = released[to].add(unreleased);
    _totalReleased = _totalReleased.add(unreleased);
    _token.transfer(to, unreleased);
  }

  function vest(address to, uint256 amount) external onlyOwner {
    require(vested[to] == 0, "TeamVesting: already vested to this address");
    require(block.timestamp < start.add(duration), "TeamVesting: vesting period is over");
    require(_token.balanceOf(address(this)) >= _totalVested.sub(_totalReleased).add(amount), "TeamVesting: not enough tokens to vest");
    vested[to] = amount;
    _totalVested = _totalVested.add(amount);
  }

  // Used to view how many tokens are due to release for an address
  function releasableAmount(address to) public view returns (uint256) {
    return _vestedAmount(to).sub(released[to]);
  }

  function _vestedAmount(address to) private view returns (uint256) {
    if (block.timestamp < start) {
      return 0;
    } else if (block.timestamp >= start.add(duration)) {
      return vested[to];
    } else {
      uint256 monthsPassed = block.timestamp
        .sub(start)
        .div(2592000);
      return vested[to].mul(monthsPassed).div(12);
    }
  }

  // Safeguard method to claim tokens that are not assigned for vesting
  function claimUnallocated(address to) external onlyOwner {
    uint256 unallocated = this.unallocatedTokens();
    require(unallocated > 0, "TeamVesting: no tokens to claim");
    _token.transfer(to, unallocated);
  }

  function unallocatedTokens() external view returns (uint256) {
    return _token.balanceOf(address(this))
      .add(_totalReleased)
      .sub(_totalVested);
  }
}
