pragma solidity 0.6.12;

contract ULOTO is ERC20, Ownable {
    using SafeMath for uint256;

    IUniswapV2Router02 public immutable uniswapV2Router;
    address public immutable uniswapV2Pair;
    
    uint256 internal _totalSupply;
    
    uint256 private lockPct = 1;                        // 1% of tx amount
    uint256 private burnPct = 50;                       // 50% of locked amount burned and rest are used to swap and liquify.
    uint256 private minHoldPct = 25;                    // caller should have over 0.25% of total supply ULOTO for call swapAndLiquify
    uint256 private minLockedAmount = 100e18;           // at least 100 ULOTO should be locked for call swapAndLiquify
    bool private swapAndLiquifyEnabled;                 // swapAndLiquifyEnabled should be true for call swapAndLiquify
    bool private inSwapAndLiquify;                      // lock while swap and liquify
    
    uint256 private maxPurchasableETH = 10e18;
    uint256 private presaleRound;
    uint256 private tokenPrice;
    mapping (address => uint256) private purchaseMap;

    event LockPctUpdated(uint256 lockPct);
    event BurnPctUpdated(uint256 burnPct);
    event MinHoldPctUpdated(uint256 minHoldPct);
    event MinLockedAmountUpdated(uint256 minLockedAmount);
    event SwapAndLiquifyEnabledUpdated();
    event PresaleRoundUpdated(uint256 presaleRound);
    event MaxPurchasableETHUpdated(uint256 maxPurchasableETH);
    event Purchased(address indexed purchaser, uint256 purchasedETH, uint256 tokenPrice);

    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );

    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    constructor(
        IUniswapV2Router02 _uniswapV2Router
    ) public ERC20("UniLoto", "ULOTO") {
        // mint tokens which will initially belong to deployer
        // deployer should go seed the pair with some initial liquidity
        _mint(msg.sender, 12000000 * 10**18);
        _mint(address(this), 8000000 * 10**18);
        

        // Create a uniswap pair for this new token
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());

        // set the rest of the contract variables
        uniswapV2Router = _uniswapV2Router;
    }
    
    /*
        override the internal _transfer function so that we can
        take the fee to lock self
    */
    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        if(swapAndLiquifyEnabled && !inSwapAndLiquify) {
            // calculate the number of tokens to lock self
            uint256 tokensToLockSelf = calculatePctAmount(
                amount,
                lockPct,
                2
            );
    
            uint256 tokensToTransfer = amount.sub(tokensToLockSelf);
    
            super._transfer(from, address(this), tokensToLockSelf);
            super._transfer(from, to, tokensToTransfer);
        } else {
            super._transfer(from, to, amount);
        }
    }

    function swapAndLiquify() public lockTheSwap {
        require(swapAndLiquifyEnabled, "ULOTO: swapAndLiquify is not allowed yet.");
        require(balanceOf(address(this)) >= minLockedAmount, "ULOTO: locked amount is less thatn minLockedAmount.");
        require(balanceOf(_msgSender()) >= calculatePctAmount(totalSupply(), minHoldPct, 4), "ULOTO: caller doesn't have enough tokens.");
        
        uint256 contractTokenBalance = balanceOf(address(this));
        uint256 burnAmount = calculatePctAmount(contractTokenBalance, burnPct, 2);
        uint256 swapAndLiquifyAmount = contractTokenBalance.sub(burnAmount);
        
        // burn tokens
        _burn(address(this), burnAmount);
        
        // split the swapAndLiquifyAmount into halves
        uint256 half = swapAndLiquifyAmount.div(2);
        uint256 otherHalf = swapAndLiquifyAmount.sub(half);

        // capture the contract's current ETH balance.
        // this is so that we can capture exactly the amount of ETH that the
        // swap creates, and not make the liquidity event include any ETH that
        // has been manually sent to the contract
        uint256 initialBalance = address(this).balance;

        // swap tokens for ETH
        swapTokensForEth(half);

        // how much ETH did we just swap into?
        uint256 newBalance = address(this).balance.sub(initialBalance);

        // add liquidity to uniswap
        addLiquidity(otherHalf, newBalance);

        emit SwapAndLiquify(half, newBalance, otherHalf);
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // add the liquidity
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(this),
            block.timestamp
        );
    }
    
    function min(uint256 value1, uint256 value2) internal pure returns (uint256) {
        if(value1 <= value2)
            return value1;
        else
            return value2;
    }
    
    function purchase() external payable {
        require(presaleRound == 1 || presaleRound == 2, 'ULOTO: presale is not enabled yet.');
        require(purchaseMap[_msgSender()] < maxPurchasableETH, 'ULOTO: you already purchased max.');
        
        uint256 purchasableETH = min(maxPurchasableETH.sub(purchaseMap[_msgSender()]), msg.value);
        uint256 purchaseTokenAmount = purchasableETH.mul(10**18).div(tokenPrice);
        uint256 purchasedETH = purchaseTokenAmount.mul(tokenPrice).div(10**18);
        uint256 refundETH = msg.value.sub(purchasedETH);
        
        purchaseMap[_msgSender()] = purchaseMap[_msgSender()].add(purchasedETH);
        _transfer(address(this), _msgSender(), purchaseTokenAmount);
        
        if(refundETH > 0) {
            (bool success,) = _msgSender().call{ value: refundETH }("");
            require(success, "refund failed");
        }
        
        Purchased(_msgSender(), purchasedETH, tokenPrice);
    }
    
    function burn() external onlyOwner {
        require(presaleRound == 3, 'ULOTO: presale is not finished yet.');
        _burn(address(this), balanceOf(address(this)));
    }
    
    /*
        calculates percentage of amount
    */
    function calculatePctAmount(
        uint256 _amount,
        uint256 _pct,
        uint256 _decimal
    ) private pure returns (uint256 pctAmount) {
        pctAmount = _amount.mul(_pct).div(10**_decimal);
    }

    receive() external payable {}

    ///
    /// Ownership adjustments
    ///

    function updateLockPct(uint256 _lockPct)
        public
        onlyOwner
    {
        lockPct = _lockPct;
        emit LockPctUpdated(_lockPct);
    }
    
    function updateBurnPct(uint256 _burnPct)
        public
        onlyOwner
    {
        burnPct = _burnPct;
        emit BurnPctUpdated(_burnPct);
    }
    
    function updateMinHoldPct(uint256 _minHoldPct)
        public
        onlyOwner
    {
        minHoldPct = _minHoldPct;
        emit MinHoldPctUpdated(_minHoldPct);
    }
    
    function updateMinLockedAmount(uint256 _minLockedAmount)
        public
        onlyOwner
    {
        minLockedAmount = _minLockedAmount;
        emit MinLockedAmountUpdated(_minLockedAmount);
    }
    
    function updateSwapAndLiquifyEnabled() public onlyOwner {
        swapAndLiquifyEnabled = true;
        emit SwapAndLiquifyEnabledUpdated();
    }
    
    function updatePresaleRound(uint256 _presaleRound) public onlyOwner {
        require(_presaleRound >= 0 && _presaleRound <= 3, 'ULOTO: presaleRound should be 0 - 3.');
        presaleRound = _presaleRound;
        if(presaleRound == 1)
            tokenPrice = 3e13;
        else if(presaleRound == 2)
            tokenPrice = 4e13;
        emit PresaleRoundUpdated(_presaleRound);
    }
    
    function updateMaxPurchasableETH(uint256 _maxPurchasableETH) public onlyOwner {
        require(_maxPurchasableETH >= 10e18, 'ULOTO: maxPurchasableETH is less than 10e18.');
        maxPurchasableETH = _maxPurchasableETH;
        emit MaxPurchasableETHUpdated(_maxPurchasableETH);
    }
    
    function withdrawETH() public onlyOwner {
        (bool success,) = _msgSender().call{ value: address(this).balance }("");
        require(success, "refund failed");
    }

    function getLockPct() public view returns (uint256) {
        return lockPct;
    }
    
    function getBurnPct() public view returns (uint256) {
        return burnPct;
    }
    
    function getMinHoldPct() external view returns (uint256) {
        return minHoldPct;
    }

    function getMinLockedAmount() external view returns (uint256) {
        return minLockedAmount;
    }
    
    function getSwapAndLiquifyEnabled() external view returns (bool) {
        return swapAndLiquifyEnabled;
    }
    
    function getPresaleRound() external view returns (uint256) {
        return presaleRound;
    }
    
    function getMaxPurchasableETH() external view returns (uint256) {
        return maxPurchasableETH;
    }
    
    function getPurchasedETH(address account) external view returns (uint256) {
        return purchaseMap[account];
    }
}
