// SPDX-License-Identifier: MIT
interface IPool {
    function rewardRate() external view returns (uint256);
    function totalSupply() external view returns (uint256);
}

interface IPool1 is IPool {
    function basisCash() external view returns (address);
}

interface IPool2 is IPool {
    function basisShare() external view returns (address);
    function lpt() external view returns (address);
}

interface ITreasury {
    function cash() external view returns (address);
    function share() external view returns (address);
    function bondOracle() external view returns (address);
}

interface IERC20t {
    function decimals() external view returns (uint8);
}

contract APY {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    address[] public pool1;
    address[] public pool2;
    address[] public erc20s;
    address public bondOracle;
    address public dai;
    address public cash;
    address public share;
    
    address public owner;

    constructor(address[] memory _pool1, address[] memory _pool2, address[] memory  _erc20s, address _treasury, address _dai) public {
        pool1 = _pool1;
        pool2 = _pool2;
        erc20s = _erc20s;
        bondOracle = ITreasury(_treasury).bondOracle();
        cash = ITreasury(_treasury).cash();
        share = ITreasury(_treasury).share();
        dai = _dai;
        
        owner = msg.sender;
    }
    
    function set(address[] memory _pool1, address[] memory _pool2, address[] memory  _erc20s, address _treasury, address _dai) public {
        require(msg.sender == owner, "not owner");
        
        pool1 = _pool1;
        pool2 = _pool2;
        erc20s = _erc20s;
        bondOracle = ITreasury(_treasury).bondOracle();
        cash = ITreasury(_treasury).cash();
        share = ITreasury(_treasury).share();
        dai = _dai;
    }
    
    function apy(uint256 sharePrice) external view returns (uint256[] memory) {
        uint n1 = pool1.length;
        uint n2 = pool2.length;

        uint[] memory res = new uint[](n1 + n2);
        for (uint i = 0; i < n1; i++) {
            res[i] = apy1(pool1[i], i);
        }

        for (uint i = n1; i < n1 + n2; i++) {
            res[i] = apy2(pool2[i-n1], sharePrice);
        }

        return res;
    }

    function apyX(uint256 index, uint256 sharePrice) external view returns (uint256) {
        if (index < pool1.length) {
            return apy1(pool1[index], index);
        } else {
            return apy2(pool2[index-pool1.length], sharePrice);
        }

        return 0;
    }

    function apy1(address pool, uint256 index) public view returns (uint256) {
        address cash = IPool1(pool).basisCash();
        uint256 cashPrice = getCashPrice();
        uint256 supply = lpSupply(pool);

        uint256 interest = rewardRate(pool).mul(cashPrice).mul(365 days).mul(10 ** _decimals(erc20s[index]));
        uint256 principle = supply.mul(1e18);
        if (principle == 0) {
            return uint256(-1);
        }
        return interest.div(principle);
    }

    function apy2(address pool, uint256 _sharePrice) public view returns (uint256) {
        address share = IPool2(pool).basisShare();
        address lpt = IPool2(pool).lpt();
        uint256 supply = lpSupply(pool);

        uint256 tokenPrice = principleLpPrice(lpt, _sharePrice);
        
        uint256 interest = rewardRate(pool).mul(_sharePrice).mul(365 days).mul(1e18);
        uint256 principle = supply.mul(tokenPrice);
        if (principle == 0) {
            return uint256(-1);
        }
        return interest.div(principle);
    }


    function getCashPrice() public view returns (uint256) {
        return IOracle(bondOracle).consult(cash, 10 ** _decimals(cash));
    }

    function principleLpPrice(address lpt, uint256 _sharePrice) public view returns (uint256) {
        IUniswapV2Pair _pair = IUniswapV2Pair(lpt);
        address t0 = _pair.token0();
        address t1 = _pair.token1();
        (uint256 r0, uint256 r1, ) = _pair.getReserves();

        uint256 p0 = 1e18;
        if (t0 == dai) {
            p0 = 1e18;
        } else if (t0 == cash) {
            p0 = getCashPrice();
        } else if (t0 == share) {
            p0 = _sharePrice;
        }

        uint256 p1 = 1e18;
        if (t1 == dai) {
            p1 = 1e18;
        } else if (t1 == cash) {
            p1 = getCashPrice();
        } else if (t1 == share) {
            p1 = _sharePrice;
        }

        uint256 v0 = p0.mul(r0).div(10 ** _decimals(t0));
        uint256 v1 = p1.mul(r1).div(10 ** _decimals(t1));

        return v0.add(v1).mul(10 ** _decimals(lpt)).div(IERC20(lpt).totalSupply());
    }

    function rewardRate(address pool) public view returns (uint256) {
        return IPool(pool).rewardRate();
    }

    function lpSupply(address pool) public view returns (uint256) {
        return IPool(pool).totalSupply();
    }

    function _decimals(address t) public view returns (uint256) {
        return IERC20t(t).decimals();
    }
}
