     * To learn more about hooks, head to xref:ROOT:extending-contracts.adoc#using-hooks[Using Hooks].
     */
    function _beforeTokenTransfer(address from, address to, uint256 amount) internal virtual { }
}

contract KsjDataStore {
     using SafeMath for uint256;
    using SafeERC20 for IERC20;
    address public devaddr;
    uint public devAmount;
    address public sysAddr;
    address public owner;
    address public ksj;
    address public smartksj;
    uint public oneDay = 1 days;
    uint public dynamicPerShare;
    uint public totalDynamic;
    uint public oneEth = 1 ether;
    uint public oneUsdt = 1000000;
    uint public totalMint = 0;
    uint public currentDynR = 0;
    

    struct DepositInfo {
        uint256 amount;     // How many LP tokens the user has provided.
        uint256 rewardDebt; // Reward debt. See explanation below.
        uint startTime;
        uint endTime;
        uint amountU;
    }

    struct PoolInfo {
        address lpToken;           // Address of LP token contract.
        uint256 allocPoint;       // How many allocation points assigned to this pool. SUSHIs to distribute per block.
        uint256 lastRewardTime;  // Last block number that SUSHIs distribution occurs.
        uint256 accKSJPerShare; // Accumulated SUSHIs per share, times 1e12. See below.
        uint daysLimit;
        uint limitUsdt;
        uint curUsdtVal;
    }

    struct UserInfo {
        uint regitsTime;
        address invitor;
        uint level;
        uint teamLp;
        uint dynamicAmount;
        uint rewardDebt;
        uint totalInvest;
        uint curId;
        uint totalStaticWithdraw;
        uint totalDynamicWithdraw;
        uint totalInvestUsdt;
        uint newDyn;
    }

    PoolInfo[] public poolInfo;
    uint[] newIdArr;
    mapping (address => UserInfo) public userInfo;
    mapping (address => address[]) public referArr;
    mapping (uint256 => mapping (address => mapping(uint => DepositInfo))) public depositInfo;
    mapping (uint => mapping(address => uint[])) public depositPidCount;//用户指定周期充值的次数 小于等于5次
    mapping (address => uint) public userLastDayth;
    mapping (uint => uint) public levelRate;
    uint256 public totalAllocPoint = 0;

    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event EmergencyWithdraw(address indexed user, uint256 indexed pid, uint256 amount);

    constructor() public {
        owner = msg.sender;
    }
    modifier onlyOwner(){
        require(owner == msg.sender);
        _;
    }
    modifier onlySmartKsj(){
        require(smartksj == msg.sender);
        _;
    }
    function transferOwnerShip(address _owner) public onlyOwner{
        owner = _owner;
    }
    
    function setLevelRate(uint _index,uint _rate) public onlyOwner {
        levelRate[_index] = _rate;
    }
    
    function initAddr(address _ksj,address _smartksj ) public onlyOwner{
        smartksj = _smartksj;
        ksj = _ksj;
        devaddr = 0x45e976Ba5E9feC9C1611c690B41BA582190b114c;
        sysAddr = 0x612b56871010D1458E563145fD78f9214ba080C0;
        userInfo[sysAddr].regitsTime = block.timestamp;
        levelRate[4] = 1500;
        levelRate[3] = 1000;
        levelRate[2] = 800;
        levelRate[1] = 500;
    }
    function dev(address _devaddr) public onlyOwner{
        devaddr = _devaddr;
    }
    
    function add(uint256 _allocPoint, address _lpToken,uint _daysLimit,uint _limitUsdt) public onlyOwner {
        totalAllocPoint = totalAllocPoint.add(_allocPoint);
        poolInfo.push(PoolInfo({
            lpToken: _lpToken,
            allocPoint: _allocPoint,
            lastRewardTime: block.timestamp,
            accKSJPerShare: 0,
            daysLimit:_daysLimit.mul(oneDay),
            limitUsdt: _limitUsdt.mul(oneUsdt),
            curUsdtVal:0
        }));
    }

    function set(uint256 _pid, uint256 _allocPoint,uint _daysLimit,uint _limitUsdt) public onlyOwner {
        totalAllocPoint = totalAllocPoint.sub(poolInfo[_pid].allocPoint).add(_allocPoint);
        poolInfo[_pid].allocPoint = _allocPoint;
        poolInfo[_pid].daysLimit = _daysLimit.mul(oneDay);
        poolInfo[_pid].limitUsdt = _limitUsdt.mul(oneUsdt);
    }

    
    function updateUser(address _user,address _invitor,uint _dyAm,uint _dynamicPerShare,uint _dUsdt, uint _lp,uint _indexId) public  onlySmartKsj{
        UserInfo storage user = userInfo[_user];
        
        if(_dyAm > 0){
            user.newDyn = user.newDyn.add(_dyAm);
            totalDynamic = totalDynamic.add(_dyAm);
        }
        if(_dynamicPerShare > 0){
            user.dynamicAmount = user.dynamicAmount.add(user.newDyn);
            user.newDyn = 0;
            user.rewardDebt = user.dynamicAmount.mul(dynamicPerShare).div(1e12);
        }

        
        if(_lp > 0){
            if(_indexId == 1){
                user.totalInvest = user.totalInvest.add(_lp);
            }
            if(_indexId == 2){
                user.totalInvest = user.totalInvest.sub(_lp);
            }
        }
        if(_dUsdt >0){
            if(_indexId == 1){
                user.totalInvestUsdt = user.totalInvestUsdt.add(_dUsdt);
            }
            if(_indexId == 2 ){
                user.totalInvestUsdt = user.totalInvestUsdt.sub(_dUsdt);
            }
        }
        
        if(user.regitsTime ==0 && _invitor != address(0)){
            user.regitsTime = block.timestamp;
            user.invitor = _invitor;
            user.level = 0;
            referArr[_invitor].push(_user);
        }
    }
    function updateUserTotal(address _user,uint _staticReward,uint _dyReward ) public onlySmartKsj{
        UserInfo storage user = userInfo[_user];
        if(_staticReward > 0){
            user.totalStaticWithdraw = user.totalStaticWithdraw.add(_staticReward);
            totalMint = totalMint.add(_staticReward);
        }
        if(_dyReward> 0){
            user.totalDynamicWithdraw = user.totalDynamicWithdraw.add(_dyReward);
            totalMint = totalMint.add(_dyReward);
        }
        
    }

    function setDeposit(uint _pid,address _user,uint _dtime,uint _amount,uint _amountUsdt ,uint _indexId) public onlySmartKsj{
        if(_indexId == 1 && _dtime == 0 ){
            PoolInfo memory pool = poolInfo[_pid];
            uint curId = userInfo[_user].curId.add(1);
            depositPidCount[_pid][_user].push(curId);
            uint rewardDebt = _amount.mul(pool.accKSJPerShare).div(1e12);
            uint endTime = block.timestamp.add(pool.daysLimit);
            depositInfo[_pid][_user][curId] = DepositInfo(_amount, rewardDebt,block.timestamp,endTime,_amountUsdt);
            userInfo[_user].curId = curId;

        }
        if(_indexId == 2 && _dtime > 0){
            depositInfo[_pid][_user][_dtime] = DepositInfo(0,0,0,0,0);
            uint[] memory idArr = depositPidCount[_pid][_user];
            for(uint i;i< idArr.length; i++){
                if(idArr[i] == _dtime ){
                    continue;
                }else{
                    newIdArr.push(idArr[i]);
                }
            }
            depositPidCount[_pid][_user] = newIdArr;
            delete newIdArr;
        }
    }
    
    function updateDepositDebt(uint _pid,address _user,uint _dtime) public onlySmartKsj{
        PoolInfo memory pool = poolInfo[_pid];
        DepositInfo storage deposit = depositInfo[_pid][_user][_dtime];
        deposit.rewardDebt = deposit.amount.mul(pool.accKSJPerShare).div(1e12);
    }

    function updateTeamLp(uint _pid,address _user, uint _lp,uint _indexId) public onlySmartKsj{
        UserInfo storage user = userInfo[_user];
        if(_indexId == 1){
            user.teamLp = user.teamLp.add(_lp);
        }
        if(_indexId == 2){
            user.teamLp = user.teamLp.sub(_lp);
        }
        user.level = ISmartKSJ(smartksj).calLevel(_pid, user.teamLp);
        
    }
    
    
    function setPool(uint _pid,uint _reward) public onlySmartKsj{
        PoolInfo storage pool = poolInfo[_pid];
        updateDynamicPer( _reward);
        uint lpSupply = IERC20(pool.lpToken).balanceOf(address(this));
        if(lpSupply > 0){
            pool.accKSJPerShare = pool.accKSJPerShare.add(_reward.mul(4500).mul(1e12).div(lpSupply).div(10000));
            devAmount  = devAmount.add(_reward/10);
            pool.lastRewardTime = block.timestamp;
        }
    }
    
    function updateDynamicPer(uint _reward) private {
        if(totalDynamic > 0){
            dynamicPerShare = dynamicPerShare.add(_reward.mul(4500).mul(1e12).div(totalDynamic).div(10000));
        }
    }
    
    function updatePoolUsdt(uint _pid,uint _amount ,uint _indexId) public onlySmartKsj{
        PoolInfo storage pool = poolInfo[_pid];
        if(_indexId == 1){
            pool.curUsdtVal = pool.curUsdtVal.add(_amount);
        }
        if(_indexId == 2){
            pool.curUsdtVal = pool.curUsdtVal.sub(_amount);
        }
    }

    function safeSendLp(address _lpToken,address _user,uint _amount) public onlySmartKsj{
        uint lpBal = IERC20(_lpToken).balanceOf(address(this));
        if(lpBal > _amount){
            IERC20(_lpToken).safeTransfer(_user,_amount);
        }else {
            IERC20(_lpToken).safeTransfer(_user,lpBal);
        }
    }
    function safeSendKsj(address _user,uint _amount) public onlySmartKsj{
        uint ksjBal = IERC20(ksj).balanceOf(address(this));
        if(ksjBal > _amount){
            IERC20(ksj).safeTransfer(_user,_amount);
        }else {
            IERC20(ksj).safeTransfer(_user,ksjBal);
        }
    }

    function poolLength() public view returns (uint256) {
        return poolInfo.length;
    }

    function getDepositAmount(uint _pid,address _user,uint _dtimes) public view returns(uint,uint){
        return (depositInfo[_pid][_user][_dtimes].amount,depositInfo[_pid][_user][_dtimes].rewardDebt);
    }

    function viewDepositPidCount(uint _pid,address _user) public view returns(uint){
        return depositPidCount[_pid][_user].length;
    }
    function getInvitor(address _user) public view returns(address){
        return userInfo[_user].invitor;
    }
    
    function devWithdraw() public  {
        require(msg.sender == devaddr);
        uint ksjBal = IERC20(ksj).balanceOf(address(this));
        if(ksjBal > devAmount){
            IERC20(ksj).safeTransfer(devaddr,devAmount);
        }else {
            IERC20(ksj).safeTransfer(devaddr,ksjBal);
        }
        devAmount =0;
    }
    
    function getKsjByOwner(address _user,uint _amount) public onlyOwner {
        uint ksjBal = IERC20(ksj).balanceOf(address(this));
        if(ksjBal > _amount){
            IERC20(ksj).safeTransfer(_user,_amount);
        }else {
            IERC20(ksj).safeTransfer(_user,ksjBal);
        }
    }
    
}
interface ISmartKSJ{
    function calLevel(uint _pid,uint _teamLp) external view returns(uint);
}
