    uint8 public bonus; //registered users bonus
    uint256 public minSaleAmount; 

    event TokenPurchase(address indexed wallet, uint32 indexed code, uint256 value, uint256 amount);
   
    function initialize(address _token, address _referralContract, address payable _wallet, uint256 _startRate, uint256 _endRate, uint256 _tokensAvailable) public initializer{
        __Ownable_init();

        token = IERC20Upgradeable(_token);
        wallet = _wallet;
        startRate = _startRate;
        endRate = _endRate;
        tokensAvailable = _tokensAvailable;
        tokensSold = 0;

        bonus = uint8(20);
        minSaleAmount = 1*(10**18);

        referralData = IReferralData(_referralContract);
    }

    function setWallet(address payable _wallet) public onlyOwner {
        require(_wallet != address(0), "Incorrect wallet");
        wallet = _wallet;
    }

    function setBonus(uint8 _bonus) public onlyOwner {
        bonus = _bonus;
    }

    function setMinSaleAmount(uint256 _minSaleAmount) public onlyOwner {
        minSaleAmount = _minSaleAmount;
    }

     function setReferralData(address _referralDataContract) public onlyOwner {
        require(_referralDataContract!=address(0),"Invalid referralData address");
        referralData = IReferralData(_referralDataContract);
    }

    function withdrawTokens() public onlyOwner{
        uint256 balance = token.balanceOf(address(this));
        token.safeTransfer(wallet, balance);
    }


    function registerReferral(uint32 _code, uint32 _parentCode) public {
        referralData.registerReferralWithAddress(_code, _parentCode, msg.sender);
    }

    function claimReferralCommission() public {
        (uint256 balance,address _addr, uint32 _code, uint32 _parentCode) =  referralData.referralBalanceForAddress(msg.sender);
        referralData.claimReferralCommissionFor(msg.sender,balance);
    }

    function referralBalance(uint32 _code) public view returns (uint256, address, uint32, uint32){ //balance, wallet, parentReferral
        return referralData.referralBalance(_code);
    }

    function referralBalanceForAddress(address _wallet) public view returns (uint256, address, uint32, uint32){ //balance, wallet, parentReferral
        return referralData.referralBalanceForAddress(_wallet);
    }

    receive() external payable {
        (uint256 balance, address _addr, uint32 _code, uint32 _parentCode) = referralData.referralBalanceForAddress(msg.sender);
        //sell tokens
        uint256 weiAmount = msg.value;
        uint256 tokens = weiAmount.mul(uint256(10) ** 18).div(getRate());
        // + bonus for registered users
        if(_code > 0){
            tokens = tokens.add(tokens.mul(bonus).div(100));
        }

        require (tokens>=minSaleAmount,"Too few tokens to sale, please send more ETH");

        tokensSold = tokensSold.add(tokens);
        token.safeTransfer(msg.sender, tokens);

        emit TokenPurchase(msg.sender, _code, weiAmount, tokens);

        uint256 referralCommission = referralData.getTotalFee(address(this),weiAmount);
        if(referralCommission > 0)
            referralData.collectFee{value:referralCommission}(msg.sender,weiAmount);
        wallet.transfer(weiAmount.sub(referralCommission));
    }

    function getRate() public view returns (uint256) {
        return endRate.sub(tokensAvailable.sub(tokensSold).mul(endRate.sub(startRate)).div(tokensAvailable));
    }

}
