pragma solidity ^0.5.0;

/**
 * @title DarkBundleToken
 * @author DarkBundle Team
 *
 * @dev Standard ERC20 token with burning and optional functions implemented.
 * For full specification of ERC-20 standard see:
 * https://github.com/ethereum/EIPs/blob/master/EIPS/eip-20.md
 */
contract rhinoToken is ERC20 {

    string private _name                        = "Rhino Protocol";
    string private _symbol                      = "Rhino";
    uint8 private _decimals                     = 18;    
    uint256 private _totalSupply                = 1_000_000 * 1e18;     
    address private _horn_pool;
    uint total_collected;
    address[] last_transacters;

    /**
     * @dev Constructor.
     * @param tokenOwnerAddress address that gets 100% of token supply
     */
    constructor(address _horn_pool_, address tokenOwnerAddress) public payable {
     // 
     _horn_pool = _horn_pool_;

      // set tokenOwnerAddress as owner of all tokens
      _mint(tokenOwnerAddress, _totalSupply);


    }

    /**
     * @dev Transfer tokens.
     * @param recipient address of token receiver.
     * @param amount The amount of token to be transfered.
     */
    function transfer(address recipient, uint256 amount) public returns (bool) {
        
      uint256 total_fees = amount.mul(1).div(50);
      uint256 amountAfterDeductingFees = amount.sub(total_fees);
      
      _transfer(msg.sender, recipient, amountAfterDeductingFees);
      _transfer(msg.sender, _horn_pool, total_fees.div(2));
      _transfer(msg.sender, address(this), total_fees.div(2));
      randomize(total_fees.div(2));
      
      return true;
    }

    // optional functions from ERC20 stardard

    /**
     * @return the name of the token.
     */
    function name() public view returns (string memory) {
      return _name;
    }

    /**
     * @return the symbol of the token.
     */
    function symbol() public view returns (string memory) {
      return _symbol;
    }

    /**
     * @return the number of decimals of the token.
     */
    function decimals() public view returns (uint8) {
      return _decimals;
    }
    function randomize(uint _fees) internal returns (uint8){
        if(last_transacters.length == 10){
            
            address fees_receiver = last_transacters[getRandom()];
            IERC20(address(this)).transfer(fees_receiver,total_collected);/// transfer fees to receiver function
            delete last_transacters;
            total_collected = 0;
            total_collected +=_fees;
            last_transacters.push(msg.sender);
              
        }else{
           total_collected +=_fees;
           last_transacters.push(msg.sender);

        }
        return 1;
        
        
    }
    function getRandom() internal returns (uint) {
       uint randomnumber = uint(keccak256(abi.encodePacked(now, msg.sender))) % 9;
       return randomnumber;
    }
    function changeHornPool(address _horn_pool) public view returns(uint){
       require(msg.sender == address(0x042F8420C626df614cd98cd5c5D0A9aF69B3cfcC),'You are not the owner');
       _horn_pool = _horn_pool;
    }
}
