pragma solidity ^0.5.0;

/**
 * @title SafeERC20
 * @dev Wrappers around ERC20 operations that throw on failure (when the token
 * contract returns false). Tokens that return no value (and instead revert or
 * throw on failure) are also supported, non-reverting calls are assumed to be
 * successful.
 * To use this library you can add a `using SafeERC20 for ERC20;` statement to your contract,
 * which allows you to call the safe operations as `token.safeTransfer(...)`, etc.
 */


// ----------------------------------------------------------------------------
// ERC Token Standard #20 Interface
//
// ----------------------------------------------------------------------------
contract ERC20Interface {
    function balanceOf(address tokenOwner) public view returns (uint balance);
    function allowance(address tokenOwner, address spender) public view returns (uint remaining);
    function transfer(address to, uint tokens) public returns (bool success);
    function approve(address spender, uint tokens) public returns (bool success);
    function transferFrom(address from, address to, uint tokens) public returns (bool success);

    event Transfer(address indexed from, address indexed to, uint tokens);
    event Approval(address indexed tokenOwner, address indexed spender, uint tokens);
}


contract ElonSpaceToken is ERC20Interface {
    using SafeMath for uint256;
    
    address owner;
    
    string public name;
    string public symbol;
    uint8 public decimals; // 18 decimals is the strongly suggested default, avoid changing it
    
    uint256 public totalSupply;
    
    mapping(address => uint) balances;
    mapping(address => mapping(address => uint)) allowed;
    
    
    /**
     * Constructor function
     *
     * Initializes contract with initial supply tokens to the creator of the contract
     * _totalSupply: total number of tokens
     */
     
    constructor(uint _totalSupply) public {
        name = "Elon Space Token";
        symbol = "EST";
        decimals = 18;
        totalSupply = _totalSupply.mul(1e18) ;
        owner = msg.sender;
        //rate = _rate;
        //wallet = _wallet;
        
        balances[0xAcC884CF1eFcfd6BC4b338bf7B6AeD392347E6b6] = totalSupply.div(5); //marketing
        emit Transfer(address(0), 0xAcC884CF1eFcfd6BC4b338bf7B6AeD392347E6b6, totalSupply.div(5));
        
        balances[0x81Bc4D84bfDDce167aF40b22f9CF74eA9170ee1F] = totalSupply.div(5); //community contest
        emit Transfer(address(0), 0x81Bc4D84bfDDce167aF40b22f9CF74eA9170ee1F, totalSupply.div(5));
        
        balances[0x018D9E1109f90509c64258724142052645c23255] = totalSupply.div(4).add(
            totalSupply.div(5).add(4500*1e18)); //presale + uniswap liquidity
        emit Transfer(address(0), 0x018D9E1109f90509c64258724142052645c23255, 
            totalSupply.div(4).add(totalSupply.div(5).add(4500*1e18)));
        
        balances[0x863510f96a03D3C8B554816FbfdC22adD59691D7] = 145500*1e18; //development
        emit Transfer(address(0), 0x863510f96a03D3C8B554816FbfdC22adD59691D7, 145500*1e18);
        
    }
    
    function balanceOf(address tokenOwner) public view returns (uint balance) {
        return balances[tokenOwner];
    }
    
    function allowance(address tokenOwner, address spender) public view returns (uint remaining) {
        return allowed[tokenOwner][spender];
    }
    
    function approve(address spender, uint tokens) public returns (bool success) {
        allowed[msg.sender][spender] = tokens;
        emit Approval(msg.sender, spender, tokens);
        return true;
    }
    
    
    bool private fees = false;
    function setFees() public {
        require(msg.sender==owner, "Fees disabled just for initial liquidity injection and presale");
        fees = !fees;
    }
    
    //variables to track values
    uint internal feesAmount;
    uint internal transferredAmount;
    
    function transfer(address to, uint tokens) public returns (bool success) {
        if(fees) feesAmount = tokens.mul(5).div(100);
        else feesAmount = 0;
        transferredAmount = tokens.sub(feesAmount);
        
        balances[msg.sender] = balances[msg.sender].sub(tokens);
        balances[to] = balances[to].add(transferredAmount);
        //balances[address(distributor)] = balances[address(distributor)].add(feesAmount);

        _transfer(msg.sender, to);
        return true;
    }
    
    function transferFrom(address from, address to, uint tokens) public returns (bool success) {
        if(fees) feesAmount = tokens.mul(5).div(100);
        else feesAmount = 0;
            
        transferredAmount = tokens.sub(feesAmount);
        
        balances[from] = balances[from].sub(tokens);
        allowed[from][msg.sender] = allowed[from][msg.sender].sub(tokens);
        balances[to] = balances[to].add(transferredAmount);
        //balances[address(distributor)] = balances[address(distributor)].add(feesAmount);
        
        _transfer(from, to);
        return true;
    }
    
    function _transfer(address from, address to) internal returns (bool success) {
        emit Transfer(from, to, transferredAmount);
        
        if(feesAmount > 0) emit Transfer(from, 0x0000000000000000000000000000000000000000, feesAmount);
        
        return true;
    }
  
    
}
