// File: contracts/CropToken.sol

pragma solidity 0.6.11;
pragma experimental ABIEncoderV2;

struct FrozenWallet {
    address wallet;
    uint256 totalAmount;
    uint256 monthlyAmount;
    uint256 initialAmount;
    uint256 startDay;
    uint256 afterDays;
    bool scheduled;
    uint256 monthDelay;
}

struct VestingType {
    uint256 monthlyRate;
    uint256 initialRate;
    uint256 afterDays;
    uint256 monthDelay;
    bool vesting;
}

contract CropToken is
    Initializable,
    OwnableUpgradeable,
    ERC20PausableUpgradeable
{
    mapping(address => FrozenWallet) public frozenWallets;
    VestingType[] public vestingTypes;

    function initialize() public initializer {
        __Ownable_init();
        __ERC20_init("Crop Token", "CROP");
        __ERC20Pausable_init();

        _mint(
            address(0x76C76680bb8A80513b0a261e8b978cDC61eAdaaf),
            6000000000000000000000000
        );
        _mint(
            address(0x5AB2Cd68A3e6F7FfEA77dE5f4991BA50F99a3E2C),
            6000000000000000000000000
        );
        _mint(
            // dev address(0xC27B0f3965B1eD78442F16E861A2bB2230f9757c),
            address(0x19a953816F5dCb7cCDb8b37bB684320304E2fD18),
            1000000000000000000000000
        );
        _mint(
            // dev address(0x6a97955673F2DEC62325800f1814014C062037F6),
            address(0x0dAE97dE55Af3243B6cD95F1Ffc3443f1da77B72),
            1050000000000000000000000
        );
        _mint(
            address(0x5Fb00CFdf85020584ec19318996bd1bAd3E13295),
            25000000000000000000000000
        );
        _mint(
            address(0x80329C32F6D28F487efdc9f6c43c4d4a6ce0493c),
            20000000000000000000000000
        );
        _mint(
            address(0x7A56Bab3FF72fDAAF044b4362d892A650325Eda8),
            20000000000000000000000000
        );

        vestingTypes.push(
            VestingType(5000000000000000000, 5000000000000000000, 0, 1, true) // %5 initial -> 19 month %5
        );
        vestingTypes.push(
            VestingType(5000000000000000000, 0, 180 days, 0, true)
        ); // 180 DaysAfter %5 20 months
    }

    function getReleaseTime() public pure returns (uint256) {
        return 1613347200; // 02/15/2021 @ 12:00am (UTC) -> 30 mart olacak
    }

    function getMaxTotalSupply() public pure returns (uint256) {
        return 100000000000000000000000000;
    }

    function addAllocations(
        address[] memory addresses,
        uint256[] memory totalAmounts,
        uint256 vestingTypeIndex
    ) external onlyOwner returns (bool) {
        require(
            addresses.length == totalAmounts.length,
            "Address and totalAmounts length must be same"
        );
        require(
            vestingTypes[vestingTypeIndex].vesting,
            "Vesting type isn't found"
        );

        VestingType memory vestingType = vestingTypes[vestingTypeIndex];
        uint256 addressesLength = addresses.length;

        for (uint256 i = 0; i < addressesLength; i++) {
            address _address = addresses[i];
            uint256 totalAmount = totalAmounts[i];
            uint256 monthlyAmount =
                totalAmounts[i].mul(vestingType.monthlyRate).div(
                    100000000000000000000
                );

            uint256 initialAmount =
                totalAmounts[i].mul(vestingType.initialRate).div(
                    100000000000000000000
                );

            uint256 afterDay = vestingType.afterDays;
            uint256 monthDelay = vestingType.monthDelay;

            addFrozenWallet(
                _address,
                totalAmount,
                monthlyAmount,
                initialAmount,
                afterDay,
                monthDelay
            );
        }

        return true;
    }

    function _mint(address account, uint256 amount) internal override {
        uint256 totalSupply = super.totalSupply();
        require(
            getMaxTotalSupply() >= totalSupply.add(amount),
            "Max total supply over"
        );

        super._mint(account, amount);
    }

    function addFrozenWallet(
        address wallet,
        uint256 totalAmount,
        uint256 monthlyAmount,
        uint256 initialAmount,
        uint256 afterDays,
        uint256 monthDelay
    ) internal {
        uint256 releaseTime = getReleaseTime();

        if (!frozenWallets[wallet].scheduled) {
            _mint(wallet, totalAmount);
        }

        // Create frozen wallets
        FrozenWallet memory frozenWallet =
            FrozenWallet(
                wallet,
                totalAmount,
                monthlyAmount,
                initialAmount,
                releaseTime.add(afterDays),
                afterDays,
                true,
                monthDelay
            );

        // Add wallet to frozen wallets
        frozenWallets[wallet] = frozenWallet;
    }

    function getTimestamp() external view returns (uint256) {
        return block.timestamp;
    }

    function getMonths(uint256 afterDays, uint256 monthDelay)
        public
        view
        returns (uint256)
    {
        uint256 releaseTime = getReleaseTime();
        uint256 time = releaseTime.add(afterDays);

        if (block.timestamp < time) {
            return 0;
        }

        uint256 diff = block.timestamp.sub(time);
        uint256 months = diff.div(30 days).add(1).sub(monthDelay);

        return months;
    }

    function isStarted(uint256 startDay) public view returns (bool) {
        uint256 releaseTime = getReleaseTime();

        if (block.timestamp < releaseTime || block.timestamp < startDay) {
            return false;
        }

        return true;
    }

    function getTransferableAmount(address sender)
        public
        view
        returns (uint256)
    {
        uint256 months =
            getMonths(
                frozenWallets[sender].afterDays,
                frozenWallets[sender].monthDelay
            );
        uint256 monthlyTransferableAmount =
            frozenWallets[sender].monthlyAmount.mul(months);
        uint256 transferableAmount =
            monthlyTransferableAmount.add(frozenWallets[sender].initialAmount);

        if (transferableAmount > frozenWallets[sender].totalAmount) {
            return frozenWallets[sender].totalAmount;
        }

        return transferableAmount;
    }

    function getRestAmount(address sender) public view returns (uint256) {
        uint256 transferableAmount = getTransferableAmount(sender);
        uint256 restAmount =
            frozenWallets[sender].totalAmount.sub(transferableAmount);

        return restAmount;
    }

    // Transfer control
    function canTransfer(address sender, uint256 amount)
        public
        view
        returns (bool)
    {
        if (!frozenWallets[sender].scheduled) {
            return true;
        }

        uint256 balance = balanceOf(sender);
        uint256 restAmount = getRestAmount(sender);

        if (
            balance > frozenWallets[sender].totalAmount &&
            balance.sub(frozenWallets[sender].totalAmount) >= amount
        ) {
            return true;
        }

        if (
            !isStarted(frozenWallets[sender].startDay) ||
            balance.sub(amount) < restAmount
        ) {
            return false;
        }

        return true;
    }

    // @override
    function _beforeTokenTransfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual override {
        require(canTransfer(sender, amount), "Wait for vesting day!");
        super._beforeTokenTransfer(sender, recipient, amount);
    }

    function pause(bool status) public onlyOwner {
        if (status) {
            _pause();
        } else {
            _unpause();
        }
    }
}
