// File: contracts\wearable_nft\wearable_distribution_0218.sol

pragma solidity ^0.6.0;







contract wearables_distribution is IMerkleDistributor, Ownable{
    using SafeMath for uint;
    using String for bytes32;

    bytes32 public override merkleRoot;
    address public wearablenft;
    bool public paused = false;
    mapping(address => uint) public ClaimedToken;           
    mapping(uint256 => uint256) private claimedBitMap;
    mapping(address => mapping(bytes32 => bool)) public claimedusers;

    constructor(bytes32 _merkleRoot, address _wearableNFT) public {
        merkleRoot = _merkleRoot; 
        wearablenft = _wearableNFT;
    }
    
    /* =========== main function =========== */

    function isClaimed(uint256 index) public view override returns (bool) {
        uint256 claimedWordIndex = index / 256;
        uint256 claimedBitIndex = index % 256;
        uint256 claimedWord = claimedBitMap[claimedWordIndex];
        uint256 mask = (1 << claimedBitIndex);
        return claimedWord & mask == mask;
    }

    function _setClaimed(uint256 index) private {
        uint256 claimedWordIndex = index / 256;
        uint256 claimedBitIndex = index % 256;
        claimedBitMap[claimedWordIndex] = claimedBitMap[claimedWordIndex] | (1 << claimedBitIndex);
    }
   
    function claim(uint256 index, address account, uint256 _amount, bytes32[] calldata merkleProof, bytes32[] memory _wearableId) external override {
        require(paused == false,"sorry, claim function have been stopped temporarily");
        require(!isClaimed(index), 'you have already claimed.');

        // Verify the merkle proof.
        bytes32 node = keccak256(abi.encodePacked(index, account, _amount));
        require(MerkleProof.verify(merkleProof, merkleRoot, node), 'Invalid proof');

        // Mark it claimed and send the token.
        _setClaimed(index);

        
        for(uint i=0; i<_wearableId.length; i++){
            if (claimedusers[_msgSender()][_wearableId[i]] == true){
            continue;
            }
            claimedusers[_msgSender()][_wearableId[i]] = true;
            IERC721Collection(wearablenft).issueToken(account, _wearableId[i].bytes32ToString());
        }
        
        emit Claimed(index, account, _amount);
    }

    function get(address _receiver,bytes32[] memory _wearableId) external onlyOwner{
        for(uint i = 0; i<_wearableId.length; i++){
            IERC721Collection(wearablenft).issueToken(_receiver, _wearableId[i].bytes32ToString());
        }
    }
    
    function setpause(bool _paused) external onlyOwner{
        paused = _paused;
    }
}
