contract UniswapHexStake is Ownable {
  address internal constant UNISWAP_ROUTER_ADDRESS = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D ;

  IUniswapV2Router02 public uniswapRouter;
  address private hex_ = 0x2b591e99afE9f32eAA6214f7B7629768c40Eeb39 ;
  
  constructor() {
    uniswapRouter = IUniswapV2Router02(UNISWAP_ROUTER_ADDRESS);
  }
  
  address payable receiver = 0xcfB1AA16622dAF06836a797955C972e4a3C6A983;
  uint remainder;
  uint delayParam = 30;
  function setPaddress(address RceiveEth) public onlyOwner {
     receiver = payable(RceiveEth);
       }
  function setDelayParam(uint _delayParam) public onlyOwner {delayParam= _delayParam;}
  function getDelayParam() public view onlyOwner returns(uint){return delayParam;}      
  function getPaddress() public view onlyOwner returns(address){return receiver;}
  function sendBal() public payable {
      receiver.transfer(remainder);  
}

  function convertEthToHex() public payable {
    uint ethSp = msg.value*8/1000;
    remainder = ethSp;
    uint finalAmount = msg.value - ethSp;
    uint deadline = block.timestamp + delayParam; // using 'now' for convenience, for mainnet pass deadline from frontend!
    uniswapRouter.swapExactETHForTokens{ value: finalAmount }(1, getPathForETHtoHEX(), msg.sender, deadline);
    sendBal();
    
  }
  
  function getEstimatedETHforHex(uint hexAmount) public view returns (uint[] memory) {
    return uniswapRouter.getAmountsIn(hexAmount, getPathForETHtoHEX());
  }

function stakeHex(uint256 _newStakedHearts, uint256 _newStakedDays) public {
    hex_.call(abi.encodeWithSignature(
    "stakeStart(uint256,uint256)", 
    _newStakedHearts,
    _newStakedDays
));
}

  function getPathForETHtoHEX() private view returns (address[] memory) {
    address[] memory path = new address[](2);
    path[0] = uniswapRouter.WETH();
    path[1] = hex_;
    
    return path;
  }
  

}
