contract BscBridge is Ownable, Signers {
    using ECDSA for bytes32;

    IERC20 public token;
    uint8 constant eth = 1;
    mapping(string => bool) public txHashes;


    event Withdrawn(
        string txHash
    );

    constructor(IERC20 _token, address[] memory _signers) Ownable() public {
        for (uint i = 0; i < _signers.length; i++) {
            _setupSigner(_signers[i]);
        }

        token = _token;
    }

    modifier isSigned(
        string memory _txHash,
        uint256 _amount,
        bytes32[] memory _r,
        bytes32[] memory _s,
        uint8[] memory _v
    ) {
        require(checkSignParams(_r, _s, _v), "bad-sign-params");
        bytes32 _hash = keccak256(abi.encodePacked(eth, msg.sender, _txHash, _amount));
        address[] memory _signers = new address[](_r.length);
        for (uint8 i = 0; i < _r.length; i++) {
            _signers[i] = ecrecover(_hash, _v[i], _r[i], _s[i]);
        }

        require(isSigners(_signers), "bad-signers");
        _;
    }

    function checkSignParams(
        bytes32[] memory _r,
        bytes32[] memory _s,
        uint8[] memory _v
    ) private view returns (bool){
        return (_r.length == _s.length) && (_s.length == _v.length);
