        string memory _txHash,
        uint256 _amount,
        bytes32[] memory _r,
        bytes32[] memory _s,
        uint8[] memory _v
    ) isSigned(_txHash, _amount, _r, _s, _v) external returns(bool){
        require(!txHashes[_txHash], "tx-hash-used");
        require(token.balanceOf(address(this)) >= _amount, "insufficient-balance");
