/**
 *
 * FT Contract (The native token of our NFT)
 * @dev Extends standard ERC20 contract
 */
contract NFCoin is Context, ERC20 {
    using SafeMath for uint256;

    // Constants
    uint256 public SECONDS_IN_A_DAY = 86400;

    uint256 public constant INITIAL_ALLOTMENT = 1830 * (10**18);

    uint256 public constant PRE_REVEAL_MULTIPLIER = 2;

    // Public variables
    uint256 public emissionStart;

    uint256 public emissionEnd;

    uint256 public emissionPerDay = 10 * (10**18);

    mapping(address => uint256) private _balances;

    mapping(address => mapping(address => uint256)) private _allowances;

    mapping(uint256 => uint256) private _lastClaim;

    uint256 private _totalSupply;

    string private _name;
    string private _symbol;
    uint8 private _decimals;
    address public _nftAddress;

    /**
     * @dev Sets the values for {name} and {symbol}, initializes {decimals} with
     * a default value of 18. Also initalizes {emissionStart}
     *
     * To select a different value for {decimals}, use {_setupDecimals}.
     *
     * All three of these values are immutable: they can only be set once during
     * construction.
     */
    constructor(
        string memory name,
        string memory symbol,
        uint256 emissionStartTimestamp
    ) ERC20(name, symbol) {
        _name = name;
        _symbol = symbol;
        _decimals = 18;
        emissionStart = emissionStartTimestamp;
        emissionEnd = emissionStartTimestamp + (86400 * 365 * 10);
    }

    /**
     * @dev When accumulated FTs have last been claimed for a NFT index
     */
    function lastClaim(uint256 tokenIndex) public view returns (uint256) {
        require(
            INFToken(_nftAddress).ownerOf(tokenIndex) != address(0),
            "Owner cannot be 0 address"
        );
        require(
            tokenIndex < INFToken(_nftAddress).totalSupply(),
            "NFT at index has not been minted yet"
        );

        uint256 lastClaimed =
            uint256(_lastClaim[tokenIndex]) != 0
                ? uint256(_lastClaim[tokenIndex])
                : emissionStart;
        return lastClaimed;
    }

    /**
     * @dev Accumulated fungible tokens for a non-fungible token index.
     */
    function accumulated(uint256 tokenIndex) public view returns (uint256) {
        require(
            block.timestamp > emissionStart,
            "Emission has not started yet"
        );
        require(
            INFToken(_nftAddress).ownerOf(tokenIndex) != address(0),
            "Owner cannot be 0 address"
        );
        require(
            tokenIndex < INFToken(_nftAddress).totalSupply(),
            "NFT at index has not been minted yet"
        );

        uint256 lastClaimed = lastClaim(tokenIndex);

        // Sanity check if last claim was on or after emission end
        if (lastClaimed >= emissionEnd) return 0;

        uint256 accumulationPeriod =
            block.timestamp < emissionEnd ? block.timestamp : emissionEnd; // Getting the min value of both
        uint256 totalAccumulated =
            accumulationPeriod.sub(lastClaimed).mul(emissionPerDay).div(
                SECONDS_IN_A_DAY
            );

        // If claim hasn't been done before for the index, add initial allotment (plus prereveal multiplier if applicable)
        if (lastClaimed == emissionStart) {
            uint256 initialAllotment =
                INFToken(_nftAddress).isMintedBeforeReveal(tokenIndex) == true
                    ? INITIAL_ALLOTMENT.mul(PRE_REVEAL_MULTIPLIER)
                    : INITIAL_ALLOTMENT;
            totalAccumulated = totalAccumulated.add(initialAllotment);
        }

        return totalAccumulated;
    }

    /**
     * @dev It is set right after deployment.
     */
    function setNFTAddress(address nftAddress) public {
        require(_nftAddress == address(0), "Already set");

        _nftAddress = nftAddress;
    }

    /**
     * @dev Burns a quantity of tokens held by the caller.
     *
     * Emits an {Transfer} event to 0 address
     *
     */
    function burn(uint256 burnQuantity) public virtual returns (bool) {
        _burn(msg.sender, burnQuantity);
        return true;
    }

    /**
     * @dev See {IERC20-transferFrom}.
     *
     * Emits an {Approval} event indicating the updated allowance. This is not
     * required by the EIP. See the note at the beginning of {ERC20}.
     *
     * Requirements:
     *
     * - `sender` and `recipient` cannot be the zero address.
     * - `sender` must have a balance of at least `amount`.
     * - the caller must have allowance for ``sender``'s tokens of at least
     * `amount`.
     */
    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public virtual override returns (bool) {
        _transfer(sender, recipient, amount);
        // Approval check is skipped if the caller of transferFrom is the NFT contract.
        if (msg.sender != _nftAddress) {
            _approve(
                sender,
                _msgSender(),
                _allowances[sender][_msgSender()].sub(
                    amount,
                    "ERC20: transfer amount exceeds allowance"
                )
            );
        }
        return true;
    }

    /**
     * @dev Claim mints FTs and supports multiple NFT indices at once.
     */
    function claim(uint256[] memory tokenIndices) public returns (uint256) {
        require(
            block.timestamp > emissionStart,
            "Emission has not started yet"
        );

        uint256 totalClaimQty = 0;
        for (uint256 i = 0; i < tokenIndices.length; i++) {
            // Sanity check for non-minted index
            require(
                tokenIndices[i] < INFToken(_nftAddress).totalSupply(),
                "NFT at index has not been minted yet"
            );
            // Duplicate token index check
            for (uint256 j = i + 1; j < tokenIndices.length; j++) {
                require(
                    tokenIndices[i] != tokenIndices[j],
                    "Duplicate token index"
                );
            }

            uint256 tokenIndex = tokenIndices[i];
            require(
                INFToken(_nftAddress).ownerOf(tokenIndex) == msg.sender,
                "Sender is not the owner"
            );

            uint256 claimQty = accumulated(tokenIndex);
            if (claimQty != 0) {
                totalClaimQty = totalClaimQty.add(claimQty);
                _lastClaim[tokenIndex] = block.timestamp;
            }
        }

        require(totalClaimQty != 0, "No accumulated NCT");
        _mint(msg.sender, totalClaimQty);
        return totalClaimQty;
    }
}
