contract SimpleAccessControl {

    address internal _owner;
    address internal _authorizedERC1155;

    modifier isOwner() {
        require(msg.sender == _owner, "ERR_NOT_OWNER");
        _;
    }

    modifier isAuthorizedERC1155() {
        require(msg.sender == _authorizedERC1155, "ERR_NOT_AUTHORIZED_ERC1155");
        _;
    }

    constructor() {
        _owner = msg.sender;
    }

    function setAuthorizedERC1155(address _erc1155) public isOwner {
        require(_erc1155 != address(0), "ERR_ZERO_ADDR");
        _authorizedERC1155 = _erc1155;
    }

    function transferOwnership(address newOwner) public isOwner {
        require(newOwner != address(0), "ERR_ZERO_ADDR");
        require(newOwner != _owner, "ERR_IS_OWNER");
        _owner = newOwner;
    }
}

