        require(_amount >= 2000, 'ERC20Crowdsale: ERR_MIN_AMOUNT');
        uint256 value = _amount * _nominalRate / 10**decimals();
        require(value <= msg.value, 'ERC20Crowdsale: ERR_NOT_ENOUGH_FUNDS');
        _mintCapped(msg.sender, _amount);
    }

    function transferToERC1155(address sender, uint256 amount)
        external
        isAuthorizedERC1155
        returns (bool)
    {
        _transfer(sender, _authorizedERC1155, amount);
        return true;
    }

    function withdrawBalance() public isOwner {
        uint256 _balance = address(this).balance;
        payable(_owner).transfer(_balance);
    }
}


