contract ERC1155CrowdsaleV2 is SimpleOwnable, ERC1155CappedV2 {
    // Address to the ERC20 token contract
    address private ERC20Token;

    // Mapping from tokenID to corresponding minting cap locks. 
    mapping (uint256 => uint32) private mintingCapLocks;

    // Evolution dependecies map
    mapping (uint256 => uint256) private evolutions;

    modifier isValidTokenID(uint256 _id) {
        require(_id > 0 && _id < 152, 'ERC1155Crowdsale: ERR_INVALID_TOKENID');
        _;
    }

    constructor(
        address _token,
        uint8[] memory _evolutions_ids,
        uint8[] memory _evolutions
    )
        ERC1155CappedV2()
    {
        ERC20Token = _token;
        for(uint8 i = 0; i < _evolutions.length; i++) {
            evolutions[_evolutions_ids[i]] = _evolutions[i];
        }
    }

    function setURI(string memory newuri) public isOwner {
        _uri = newuri;
    }
    /**
     * Implements crowdsale token minting protocol.
     */
    function mintCrowdsale(uint8 _id, uint32 _amount) public payable isValidTokenID(_id) {
        require(mintingCapLocks[_id] > 0, 'ERC1155Crowdsale: ERR_INACTIVE_CROWDSALE');
        uint256 price = _amount * _calculateTokenRate(_id);
        (bool success,) = address(ERC20Token).call{ value: msg.value }(
            abi.encodeWithSignature("mintCrowdsale(uint256)", price)
        );
        require(success, 'ERC1155Crowdsale: ERR_ERC20_CALL');
        require(totalSupply[_id] + _amount <= mintingCapLocks[_id], 'ERC1155Crowdsale: ERR_EXCEEDED_CROWDSALE_SUPPLY');
        _mintCapped(msg.sender, _id, _amount);
    }

    /**
     * Implements normal token minting protocol.
     */
    function mintNominal(uint8 _id, uint32 _amount) public isValidTokenID(_id) {
        require(evolutions[_id] == 0, 'ERC1155Crowdsale: ERR_CANT_MINT_EVOLUTION');
        uint256 price = _amount * _calculateTokenRate(_id);
        ERC20Crowdsale(ERC20Token).transferToERC1155(msg.sender, price);
        _mintCapped(msg.sender, _id, _amount);
    }

    /**
     * Implements evolution token minting protocol.
     */
    function mintEvolution(uint8 _id, uint32 _amount) public isValidTokenID(_id) {
        require(evolutions[_id] != 0, 'ERC1155Crowdsale: ERR_NOT_EVOLUTION');
        uint256 total = _amount * _calculateEvolutionRate(_id);
        require(_balances[evolutions[_id]][msg.sender] >= total, 'ERC1155Crowdsale: ERR_NOT_ENOUGH_TOKENS');
        _burn(msg.sender, evolutions[_id], total);
        _mintCapped(msg.sender, _id, _amount);
    }

    function mintAward() public {
        uint256[] memory ids = new uint256[](150);
        uint256[] memory amounts = new uint256[](150);

        for(uint8 i = 1; i < 151; i++) {
            require(_balances[i][msg.sender] >= 2);
            amounts[i-1] = 1;
            ids[i-1] = i;
        }

        _burnBatch(msg.sender, ids, amounts);
        _mintCapped(msg.sender, 151, 1);
    }

    function swapFromToken(uint8 _id, uint256 _amount) public isValidTokenID(_id) {
        uint256 _price = _amount * _calculateTokenRate(_id);
        ERC20Crowdsale(ERC20Token).transferToERC1155(msg.sender, _price);
        _transfer(address(this), msg.sender, _id, _amount);
    }

    function swapToToken(uint8 _id, uint256 _amount) public isValidTokenID(_id) {
        uint256 _price = _amount * _calculateTokenRate(_id);
        ERC20Crowdsale(ERC20Token).transfer(msg.sender, _price);
        _transfer(msg.sender, address(this), _id, _amount);
    }

    function setMintingCapLocks(uint8[] calldata _ids, uint32[] calldata _locks) public isOwner () {
        for(uint8 i = 0; i < _ids.length; i++) {
            mintingCapLocks[_ids[i]] = _locks[i];
        }
    }

    function _calculateTokenRate(uint8 _id) internal view returns (uint256) {
        return 4537500000000000000000000 / maxSupply[_id];
    }

    function _calculateEvolutionRate(uint8 _id) internal view returns (uint32) {
        return maxSupply[evolutions[_id]] / maxSupply[_id];
    }
}
