pragma solidity 0.6.12;








contract FeiSeller is Ownable, Pausable {
    using SafeMath for uint256;

    event Sell(address indexed receiver, uint feiAmount, uint ethAmount);

    // * 1e18
    uint256 public targetPrice;
    // * 1e18
    uint256 public minSellAmount;
    address public receiver;

    // eth/fei pair
    IUniswapV2Pair public constant PAIR = IUniswapV2Pair(0x94B0A3d511b6EcDb17eBF877278Ab030acb0A878);
    IUniswapV2Factory public constant FACTORY = IUniswapV2Factory(0x5C69bEe701ef814a2B6a3EDD4B1652CB9cc5aA6f);
    IFeiRouter public constant ROUTER = IFeiRouter(0x9271D303b57c204636C38Df0eD339b18Bf98f909);
    // IUniswapPCVController public constant PCV = IUniswapPCVController(0x7a165F8518A9Ec7d5DA15f4B77B1d7128B5D9188);
    IERC20 public constant WETH = IERC20(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2);
    IERC20 public constant FEI = IERC20(0x956F47F50A910163D8BF957Cf5846D573E7f87CA);
    IERC20 public constant USDC = IERC20(0xA0b86991c6218b36c1d19D4a2e9Eb0cE3606eB48);
    address public constant ETH_USDC_POOL = 0xB4e16d0168e52d35CaCD2c6185b44281Ec28C9Dc;

    constructor(
        uint256 _targetPrice,
        uint256 _minSellAmount,
        address _receiver
    ) public {
        targetPrice = _targetPrice;
        minSellAmount = _minSellAmount;
        receiver = _receiver;
    }

    function setTargetPrice(uint256 _targetPrice) external {
        require(msg.sender == receiver, "!receiver");
        targetPrice = _targetPrice;
    }

    function setMinSellAmount(uint256 _minSellAmount) external {
        require(msg.sender == receiver, "!receiver");
        minSellAmount = _minSellAmount;
    }

    function setReceiver(address _receiver) external {
        require(msg.sender == receiver, "!receiver");
        receiver = _receiver;
    }

    function ethPriceInUSD() public view returns (uint256) {
        return USDC.balanceOf(ETH_USDC_POOL).mul(1e12).mul(1e18).div(WETH.balanceOf(ETH_USDC_POOL));
    }

    function feiPriceInUSD() public view returns (uint256) {
        return WETH.balanceOf(address(PAIR)).mul(ethPriceInUSD()).div(FEI.balanceOf(address(PAIR)));
    }

    function sellFeiAmount() public view returns (uint256) {
        (uint256 feiReserve, uint256 ethReserve,) = PAIR.getReserves();
        uint256 maxSellAmount = ethReserve.mul(ethPriceInUSD()).div(targetPrice).sub(feiReserve.mul(1000).div(997)).sub(1e18);
        uint256 bal = FEI.balanceOf(address(this));
        return bal > maxSellAmount ? maxSellAmount : bal;
    }

    function sellFei() external onlyOwner {
        uint256 sellAmount = sellFeiAmount();
        require(sellAmount > minSellAmount, "!minSellAmount");

        FEI.approve(address(ROUTER), sellAmount);
        uint256 amountOut = ROUTER.sellFei(0, sellAmount, 0, receiver, now.add(60));
        uint256 actualTargetPrice = amountOut.mul(ethPriceInUSD()).div(sellAmount);
        require(actualTargetPrice >= targetPrice, "!targetPrice");

        emit Sell(receiver, sellAmount, amountOut);
    }

    function withdraw(address token) external {
        require(msg.sender == receiver, "!receiver");

        if (token == address(0)) {
            payable(receiver).transfer(address(this).balance);
            return;
        }

        IERC20(token).transfer(receiver, IERC20(token).balanceOf(address(this)));
    }
}
