   */
  function _burn(
    uint256 _tokenId
  )
    internal
    override
    virtual
  {
    super._burn(_tokenId);

     
  }
   
 
 
  
  function _setTokenProjectId(
    uint256 _tokenId,
    uint256 _projectId
  )
    internal
    validNFToken(_tokenId)
  {
    tokenIdToProjectId[_tokenId] = _projectId;
  }

  
   function _setProjectUri(
    uint256 _projectId,
    string memory _uri
  )
    internal
     
  { 
    
   
