contract Beats is Ownable, ERC721, VRFConsumerBase {
    using SafeMathCopy for uint256;

    // Constants
    uint256 constant MAX_SEED_NUMBER = 15;
    // As index starts from 1, so the array size is MAX_SEED_NUMBER+1
    uint256[MAX_SEED_NUMBER+1]  MAX_PRINT_NUMBERS = [uint256(0), 200, 200, 200, 200, 200,
                                                     200, 200, 200, 200, 200, 
                                                     200, 200, 200, 200, 200];
    // the maximum number is the sum of all prints
    uint256 constant MAX_NFT_NUMBER = 3000;
    uint256 constant MAX_SCORE = 10;
    // All the index starts from 1
    uint256 constant STARTING_INDEX = 1;
    // Charge a mark up in case of price collision, refund the user if overcharged
    uint256 constant MINT_MARK_UP = uint256(10 ** 16); // 0.01 ETH, 1 ETH = 10 ** 18
    // Merge n pieces costs: (n - 1) * merge_fee_unit
    uint256 constant MERGE_FEE_UNIT = 0;

    // Draw in a pack
    uint256 constant PACK_NUMBER = 10;
    // pack sale discount
    uint256 constant PACK_SALE_DISCOUNT = 50;

    // Total number of NFTs minted
    uint256 public mintedNumber = 0;
    // Total number of NFTs minted for each seed
    mapping(uint256 => uint256) public seedMintedNumber;
    // Total number of NFTs sold
    uint256 public soldNumber = 0;

    // Total number of NFTs burnt
    uint256 public burntNumber = 0;
    // Total number of NFTs burnt for each seed
    mapping(uint256 => uint256) public seedBurntNumber;

    // The user actions are enabled or not
    bool enabled = true;

    // rewards pool 
    uint256 public rewardPool = 0;
    // percentage of fee goes into rewards pool
    uint256 constant REWARD_POOL_CUT = 20;
    // winner cut
    uint256 constant WINNER_CUT = 50;

    // referral cut
    uint256 constant REFERRAL_CUT = 20;
    // referral mapping
    mapping(string => address) internal referrals;
    // referrals number
    uint256 public referralNumber = 0;
    // max referral number
    uint256 constant MAX_REFERRAL_NUMBER = 1000;

    // chain.link
    // mainnet price is 2 LINK
    uint256 internal LINK_FEE = uint256(10 ** 18).safeMul(2); // 2 LINK, 1 LINK = 10 ** 18
    address internal VRF_COORDINATOR;
    address internal LINK_TOKEN;
    bytes32 internal KEY_HASH;

    struct VrfCallbackData {
        bytes32 requestID;
        uint256 randomness;
    }

    // the queue of vrf callback data
    VrfCallbackData[] vrfCallbackDatas;
    // the index of vrfCallbackDatas to be consumed next
    uint256 internal vrfCallbackDataIndex = 0;
    // maximum minted number to consumed together, to limit the gas usage
    uint256 constant MAX_MINTS_IN_SINGLE_TRANSACTION = 10;

    // MintContext stores the context of a mint
    struct MintContext {
        address payable toAddress;
        uint256 price;
        uint256 packSize;
    }

    // The orders waiting to be fulfilled
    //  key: chain.link randome generator request ID
    //  value: the mint order context
    mapping(bytes32 => MintContext) internal waitingOrders;

    // Events for testing
    event RNGRequest(bytes32 requestId);

    constructor(string memory _name,
                string memory _symbol,
                address _vrfCoordinator,
                address _linkToken,
                bytes32 _keyHash)
                    ERC721(_name, _symbol) 
                    VRFConsumerBase(_vrfCoordinator, _linkToken) public {
        console.log("operation:deploy sender:%s", msg.sender);

        VRF_COORDINATOR = _vrfCoordinator;
        LINK_TOKEN = _linkToken;
        KEY_HASH = _keyHash;
    }

    /****************
        Modifier
     ****************/

    modifier onlyVrfCoordinator() {
        require(VRF_COORDINATOR == msg.sender, "VRF coordinator: caller is not the VRF coordinator");
        _;
    }

    modifier onlyWhenEnabled() {
        require(enabled, "Contract is disabled");
        _;
    }

    /*******************************
     *   Public facing functions
     *******************************/
    
    /**
        order function
        1. user could order either 1 piece or 10 pieces together
        2. the order process is async, user pay the price and pre-order, 
            the system deliver the orders async after receiving random numbers
     */
    function order(uint256 packSize, string memory referralCode) public payable onlyWhenEnabled {
        console.log("operation:order sender:%s value:%s pack:%s", msg.sender, msg.value, packSize);

        require(
            packSize == 1 || packSize == PACK_NUMBER,
            "Invalid pack size"
        );

        require(
            soldNumber + packSize <= MAX_NFT_NUMBER,
            "Max supply reached"
        );

        uint256 price;
        if (packSize == 1) {
            price = getLatestPrice();
        } else {
            price = getLatestPackPrice();
        }
        console.log("price:%s", price, " value:", msg.value);
        require(
            msg.value >= price,
            "Insufficient payment"
        );
        
        bytes32 requestID = _getRandomNumber(soldNumber);
        waitingOrders[requestID] = MintContext(msg.sender, price, packSize);
        soldNumber = soldNumber.safeAdd(packSize);

        // Only for testing
        emit RNGRequest(requestID);

        // refund the overcharge value
        if (msg.value.safeSub(price) > 0) {
            (bool success, ) =
                msg.sender.call{value: msg.value.safeSub(price)}("");
            require(success, "Refund failed");

            console.log("operation:refund user:%s value:%s", msg.sender, msg.value.safeSub(price));
        }

        // pay referral
        _fundReferral(referralCode, price);
    }

    /**
        merge function:
        1. user could merge pieces of same type together
        2. the score is the sum of all pieces
        3. the price is (n - 1) * merge_unit_price
     */
    function merge(uint256[] memory tokens, string memory referralCode) public payable onlyWhenEnabled {
        // at least merge two tokens
        require(
            tokens.length > 1,
            "At least merge two tokens"
        );

        // all tokens belong to the msg.sender
        for (uint256 i = 0; i < tokens.length; i = i.safeAdd(1)) {
            address owner = ownerOf(tokens[i]);
            require(
                owner == msg.sender,
                "Some token doesn't belong to the sender"
            );
        }

        // all tokens belong to the same category
        uint256 seedIndex = _getSeedIndex(tokens[0]);
        for (uint256 i = 1; i < tokens.length; i = i.safeAdd(1)) {
            require(
                seedIndex == _getSeedIndex(tokens[i]),
                "Some tokens are not from the same category"
            );
        }

        // pay the right price
        uint256 price = getMergePrice(tokens.length);
        console.log("merge price:", price, "value:", msg.value);
        require(
            msg.value == price,
            "Insufficient payment"
        );

        // the new token inherits the smallest print index
        uint256 printIndex = _getPrintIndex(tokens[0]);
        for (uint256 i = 1; i < tokens.length; i = i.safeAdd(1)) {
            uint256 candidate = _getPrintIndex(tokens[i]);
            if (candidate < printIndex) {
                printIndex = candidate;
            }
        }

        // the new score is the sum of all merged pieces
        //  - but the score has a maximum value
        uint256 score = 0;
        for (uint256 i = 0; i < tokens.length; i = i.safeAdd(1)) {
            uint256 eachScore = _getScore(tokens[i]);
            require(
                eachScore < MAX_SCORE,
                "Cannot merge a full score token"
            );
            score += eachScore;
        }
        if (score > MAX_SCORE) {
            score = MAX_SCORE;
        }

        // burn the old tokens
        for (uint256 i = 0; i < tokens.length; i = i.safeAdd(1)) {
            _burn(tokens[i]);
        }
        burntNumber += tokens.length;
        seedBurntNumber[seedIndex] = seedBurntNumber[seedIndex].safeAdd(tokens.length);

        // mint the new token
        uint256 token = _genToken(msg.sender, seedIndex, printIndex, score);
        _mint(msg.sender, token);
        
        // update reward pool
        _addRewardPool(price);

        // pay referral
        _fundReferral(referralCode, price);

        // console.log("operation:merge sender:%s price:%s token:%s seedIndex:%s printIndex:%s score:%s burntNumber:%s",
        //     msg.sender, price, token, seedIndex, printIndex, score, burntNumber);
    }

    /**
        register to the referral program
     */
    function registerReferralCode() public {
        string memory code = _genReferral(msg.sender);
        address record = referrals[code];
        require(
            record == address(0x0),
            "Already registered"
        );
        require(
            referralNumber < MAX_REFERRAL_NUMBER,
            "Referral code all sold out"
        );
        referrals[code] = msg.sender;
        referralNumber = referralNumber.safeAdd(1);
        console.log("operation:registerReferralCode sender:", msg.sender, "code:", code);
    }

    /**
        getLatestPrice: returns the price of the next mint
            throws error if sold out
     */
    function getLatestPrice() public view returns (uint256) {
        return _getMintPrice(soldNumber.safeAdd(1));
    }

    /**
        getLatestPackPrice: return the price of the next pack mint
            it's the sum price of the next PACK_NUMBER single mints
            throws error if the remaining supply is less than PACK_NUMBER
     */
    function getLatestPackPrice() public view returns (uint256) {
        uint256 price = 0;
        for (uint256 i = 1; i <= PACK_NUMBER; i = i.safeAdd(1)) {
            price = price.safeAdd(_getMintPrice(soldNumber.safeAdd(i)));
        }
        price = price.safeMul(PACK_SALE_DISCOUNT).safeDiv(100);
        return price; 
    }

    /**
        getPackSize: return the pack size
            user could buy PACK_NUMBER mints in a single transaction
     */
    function getPackSize() public pure returns (uint256) {
        return PACK_NUMBER;
    }

    /**
        getMaxScore: return the maximum score of merging pieces together
     */
    function getMaxScore() public pure returns (uint256) {
        return MAX_SCORE;
    }

    /**
        getOrderMarkup: the markup the user pay for each transaction
            it's used to avoid the price collision when multiple users buy at the same time
            the overcharged markup will be refunded right after the transaction
     */
    function getOrderMarkup() public pure returns (uint256) {
        return MINT_MARK_UP;
    }

    /**
        getMergePrice: return the price of merging n pieces
            price = (n - 1) * unit_price
     */
    function getMergePrice(uint256 n) public pure returns (uint256) {
        require(
            n >= 2,
            "Must merge at least 2 pieces together"
        );

        return MERGE_FEE_UNIT.safeMul(n.safeSub(1));
    }

    /**
        getTokenInfo: return (seed_index, print_index, score) from token
            the first, second, third contents of 8 bits
     */
    function getTokenInfo(uint256 token) public pure returns (uint256, uint256, uint256) {
        return (_getSeedIndex(token), _getPrintIndex(token), _getScore(token));
    }

    /**
        getReferralCode: return the referral code for the sender if they registered
     */
    function getReferralCode() public view returns (string memory) {
        string memory code = _genReferral(msg.sender);
        address recordAddress = referrals[code];
        console.log("operation:getReferralCode sender:", msg.sender, "code:", code);
        require(
            msg.sender == recordAddress,
            "Not registered to the referral program"
        );
        return code;
    }

    /**************************
     * Relay facing functions
     **************************/

    /**
        canMint function
        1. only be able to mint afte receiving random numbers
     */
    function canMint() public view returns (bool) {
        return vrfCallbackDatas.length > 0;
    }

    /**
        doMint function
        1. consume all the random numbers and mint the pieces
     */
    function doMint() public {
        uint256 totalPrice = 0;
        uint256 mintedTogether = 0;

        for (uint256 i = vrfCallbackDataIndex; i < vrfCallbackDatas.length; i = i.safeAdd(1)) {
            bytes32 requestID = vrfCallbackDatas[i].requestID;
            uint256 randomness = vrfCallbackDatas[i].randomness;

            MintContext memory mintContext = waitingOrders[requestID];
            address toAddress = mintContext.toAddress;
            uint256 packSize = mintContext.packSize;
            uint256 price = mintContext.price;
            // remove the request from map
            delete(waitingOrders[requestID]);
            console.log("operation:doMint packSize:%s price:%s toAddress:%s", packSize, price, toAddress);
            
            if (toAddress == address(0x0)) {
                continue;
            }

            for (uint256 index = 0; index < packSize; index = index.safeAdd(1)) {
                randomness = uint256(keccak256(abi.encodePacked(block.number, blockhash(block.number - 1), toAddress, randomness)));
                _mintOne(toAddress, randomness);
                mintedTogether = mintedTogether.safeAdd(1);
            }

            totalPrice = totalPrice.safeAdd(price);

            vrfCallbackDataIndex = i.safeAdd(1);
            // limit the mints in a transaction to limit the gas usage
            if (mintedTogether >= MAX_MINTS_IN_SINGLE_TRANSACTION) {
                console.log("operation:breakMint mintedTogether:%s vrfIndex:%s vrfLength:%s", mintedTogether, vrfCallbackDataIndex, vrfCallbackDatas.length);
                break;
            }
        }

        // reset the queue when reaching to the end
        if (vrfCallbackDataIndex == vrfCallbackDatas.length) {
            delete(vrfCallbackDatas);
            vrfCallbackDataIndex = 0;
            console.log("operation:clearVrfData");
        }

        _addRewardPool(totalPrice);
    }

    /****************************************
        Admin functions, only owner can run
     ****************************************/

    /**
        setEnabled: enable/disable the contract user interface
     */
    function setEnabled(bool _enabled) public onlyOwner {
        enabled = _enabled;
    }

    /**
        setBaseURI: set the base URI
     */
    function setBaseURI(string memory baseURI_) public onlyOwner {
        _setBaseURI(baseURI_);
    }

    /**
        withdraw: withdraw ETF to owner
     */
    function withdraw(uint256 withdrawAmount) public onlyOwner {
        console.log("withdraw amount:", withdrawAmount, "sender:", msg.sender);
        (bool success, ) = msg.sender.call{value: withdrawAmount}("");
        require(success, "Payment to owner failed");
    }

    /***********************
     * Internal functions
     ***********************/

    /**
        _fundReferral: fund price * REFERRAL_CUT% to the referer
     */
    function _fundReferral(string memory referralCode, uint256 price) internal {
        address toAddress = referrals[referralCode];
        if (toAddress != address(0x0)) {
            uint256 referralCut = price.safeMul(REFERRAL_CUT).safeDiv(100);
            (bool success, ) = payable(toAddress).call{value: referralCut}("");
            require(success, "Payment to referral failed");
        }
    }

    /**
        _addRewardPool: add fee * REWARD_POOL_CUT% into pool
     */
    function _addRewardPool(uint256 fee) internal {
        rewardPool = rewardPool.safeAdd(fee.safeMul(REWARD_POOL_CUT).safeDiv(100));
    }

    function _rewardWinner(address toAddress) internal {
        uint256 winnerCut = rewardPool.safeMul(WINNER_CUT).safeDiv(100);
        (bool success, ) = payable(toAddress).call{value: winnerCut}("");
        require(success, "Payment to seed owner failed");
        rewardPool = rewardPool.safeSub(winnerCut);
    }

    /**
        _mintOne function:
        1. generate the NFT
     */
    function _mintOne(address toAddress, uint256 randomness) internal {
        // Check supply
        require(
            mintedNumber < MAX_NFT_NUMBER,
            "Insufficient supply"
        );

        uint256 seedIndex;
        uint256 printIndex;
        (seedIndex, printIndex) = _calc(randomness);
        uint256 score = 1;
        uint256 token = _genToken(toAddress, seedIndex, printIndex, score);
        console.log("minted seedIndex is '%s' printIndex is '%s' token is '%s'", seedIndex, printIndex, token);

        _mint(toAddress, token);
        mintedNumber = mintedNumber.safeAdd(1);
        seedMintedNumber[seedIndex] = seedMintedNumber[seedIndex].safeAdd(1);
    }

    /**
        _calc: calculate the seed index and print index from the random number
     */
    function _calc(uint256 randomness) internal view returns (uint256, uint256) {
        assert(mintedNumber < MAX_NFT_NUMBER);
        uint256 n = randomness.safeMod(MAX_NFT_NUMBER.safeSub(mintedNumber)).safeAdd(1);
        for (uint256 seedIndex = STARTING_INDEX; seedIndex <= MAX_SEED_NUMBER; seedIndex = seedIndex.safeAdd(1)) {
            uint remainingPrintNumber = MAX_PRINT_NUMBERS[seedIndex].safeSub(seedMintedNumber[seedIndex]);
            if (n <= remainingPrintNumber) {
                uint256 printIndex = seedMintedNumber[seedIndex].safeAdd(1);
                return (seedIndex, printIndex);
            }
            n = n.safeSub(remainingPrintNumber);
        }
        // should not reach here
        assert(false);
    }

    /**
        _getMintPrice: return the price of the mintedIndex-th mint
            price = mintedIndex / max_nft_number * 0.7 + 0.3
            overall price range is from 0.3 to 1.0
     */
    function _getMintPrice(uint256 mintedIndex) internal pure returns (uint256) {
        require(
            mintedIndex <= MAX_NFT_NUMBER,
            "Maximum supply exceeded"
        );

        uint256 a = 0.9 ether;
        uint256 b = 0.1 ether;
        return b.safeAdd(a.safeMul(mintedIndex).safeDiv(MAX_NFT_NUMBER));
    }

    /**
        _genReferral: returns the referral code for an address
            the same address will always get the same referral code
     */
    function _genReferral(address toAddress) internal pure returns (string memory) {
        bytes32 hash = keccak256(abi.encodePacked(toAddress));
        return _substr(_uint2str(uint256(hash)), 8);
    }

    /**
     * _genToken generates unique token for each NFT
     *  - 1st byte: seedIndex
     *  - 2nd byte: printIndex
     *  - 3rd byte: score
     *  - 4th byte: zero (reserved byte)
     */
    function _genToken(address toAddress, uint256 seedIndex, uint256 printIndex, uint256 score) internal view returns (uint256) {
        bytes32 hash = keccak256(abi.encodePacked(block.number, blockhash(block.number - 1), toAddress, seedIndex, printIndex, score));
        uint256 token = 0;
        for (uint256 i = 0; i < 32; i = i.safeAdd(1)) {
            token = token << 8;
            if (i == 0) {
                token = token | seedIndex;
            } else if (i == 1) {
                token = token | printIndex;
            } else if (i == 2) {
                token = token | score;
            } else if (i == 3) {
                // do nothing, reversed byte
            } else {
                token = token | uint8(hash[i]);
            }
        }
        return token;
    }

    /**
        _getSeedIndex: return the seed index from the token
            the first 8 bits
     */
    function _getSeedIndex(uint256 token) public pure returns (uint256) {
        return token >> 248;
    }

    /**
        _getPrintIndex: return the print index from the token
            the second 8 bits
     */
    function _getPrintIndex(uint256 token) public pure returns (uint256) {
        return (token >> 240) & 0xff;
    }

    /**
        _getScore: return the score from the token
            the third 8 bits
     */
    function _getScore(uint256 token) public pure returns (uint256) {
        return (token >> 232) & 0xff;
    }

    /**
     *  _beforeTokenTransfer: the hook to be called before every token transfer
     *      it includes token transfer between users and also token mint from platform
     *      record the seed owner of each category
     */
    function _beforeTokenTransfer(address from, address to, uint256 token) internal virtual override {
        super._beforeTokenTransfer(from, to, token);

        uint256 score = _getScore(token);
        if (from == address(0x0) && score == MAX_SCORE) {
            _rewardWinner(to);
        }
    }

    /*********************************
        chain.link VRF random numbers
     *********************************/

    /** 
        _getRandomNumber: send the request number request to chain.link
     */
    function _getRandomNumber(uint256 seed) internal returns (bytes32) {
        uint256 linkBalance = LINK.balanceOf(address(this));
        require(
            linkBalance >= LINK_FEE, 
            "Contract doesn't have enough LINK"
        );
        console.log("operation:callChainLink seed:%s balance:%s", seed, linkBalance);
        return requestRandomness(KEY_HASH, LINK_FEE, seed);
    }

    /**
        fulfillRandomness: the callback function of chain.link
     */
    function fulfillRandomness(bytes32 requestID, uint256 randomness) internal override onlyVrfCoordinator {
        vrfCallbackDatas.push(VrfCallbackData(requestID, randomness));
        console.log("operation:chainLinkCallback sender:%s randomness:%s", msg.sender, randomness);
    }

    /***********************************|
    |    Utility Internal Functions     |
    |__________________________________*/

    /**
    * @notice Convert uint256 to string
    * @param _i Unsigned integer to convert to string
    */
    function _uint2str(uint256 _i) internal pure returns (string memory _uintAsString) {
        if (_i == 0) {
        return "0";
        }

        uint256 j = _i;
        uint256 ii = _i;
        uint256 len;

        // Get number of bytes
        while (j != 0) {
        len++;
        j /= 10;
        }

        bytes memory bstr = new bytes(len);
        uint256 k = len - 1;

        // Get each individual ASCII
        while (ii != 0) {
        bstr[k--] = byte(uint8(48 + ii % 10));
        ii /= 10;
        }

        // Convert to string
        return string(bstr);
    }

    /**
        return the substring which has length of k
     */
    function _substr(string memory _str, uint256 k) internal pure returns (string memory) {
        bytes memory bstr = bytes(_str);
        if (k >= bstr.length) {
            return _str;
        }
        
        bytes memory bsubstr = new bytes(k);
        for (uint i = 0; i < k; i++) {
            bsubstr[i] = bstr[i];
        }
        return string(bsubstr);
    }
}
