// File: contracts\TICNFT.sol

// SPDX-License-Identifier: MIT

pragma solidity ^0.7.4;







contract TICNFT is ERC1155, IERC1155TokenReceiver, IERC1155Metadata, Ownable {

	using SafeMath for uint256;

	

	string public name = "TICNFT";

    string public symbol = "TIC";

	

	uint256 pricePerNFT = 50000000000000; //In Wei, worth ~9 cents currently

	uint256 tokenIDCounter;



	uint256 pricePerDemo = 5000000000000000; //In Wei

	uint256 demoID = 1;

	

	mapping(uint256 => string) tokenURIs;

	

    function mintNFT(address recipient,string calldata tokenURI) external payable {

		require(recipient != address(0),"b");

		uint256 count = msg.value.div(pricePerNFT);

		require(count > 0,"0");

		

        uint256 tokenID = ++tokenIDCounter; //Increment counter and then set it to tokenID

		balances[recipient][tokenID] = balances[recipient][tokenID].add(count);

		tokenURIs[tokenID] = tokenURI;

		emit TransferSingle(msg.sender,address(0),recipient,tokenID,count);

    }

	

	function buyDemoNFT() external payable {

		uint256 count = msg.value.div(pricePerDemo);

		require(count > 0,"0");

		_safeTransferFrom(address(this),address(msg.sender),demoID,count);

	}

	

	function uri(uint256 tokenID) external view override returns (string memory) {

		return tokenURIs[tokenID];

	}

	

	function withdraw() external onlyOwner { //Extract all funds

		require(msg.sender.send(address(this).balance));

	}

	

	function setPricePerNFT(uint256 newPrice) external onlyOwner {

		pricePerNFT = newPrice;

	}

	

	function setVendingDemo(uint256 id,uint256 pricePer) external onlyOwner {

		demoID = id;

		pricePerDemo = pricePer;

	}

	

	function onERC1155Received(address, address, uint256, uint256, bytes calldata) external pure override returns(bytes4) {

		return ERC1155_RECEIVED_VALUE;

	}

	function onERC1155BatchReceived(address, address, uint256[] calldata, uint256[] calldata, bytes calldata) external pure override returns(bytes4) {

		return ERC1155_BATCH_RECEIVED_VALUE;

	}

	

	function getPricePerNFT() external view returns (uint256) {

		return pricePerNFT;

	}

	

	function getDemoID() external view returns (uint256) {

		return demoID;

	}

	

	function getDemoPrice() external view returns (uint256) {

		return pricePerDemo;

	}

	

	function getLatestTokenID() external view returns (uint256) {

		return tokenIDCounter;

	}

}
