pragma solidity ^0.6.0;

pragma experimental ABIEncoderV2;






contract LaikaLockStrategy is Governance {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    IERC20 public _laika;

    uint256 private _balance;
    
    address private _caller;
    address private _locker;

    uint256 public _lockPeriod = 365 days;

    struct LockData {
        uint256 startTime;
        uint256 amount;
        bool claimed;
    }

    uint256 private _nextLockId;
    uint256 private _nextCliamId;

    mapping(uint256 => LockData) private _lockTable;


    constructor (IERC20 laika) public {
        _laika = laika;
        _locker = 0xE08479E3935511e78CF511C1e8c6B1DC47dDcc71;
    }

    function balance() public view returns (uint256) {
        return _balance;
    }

    function caller() public view returns (address) {
        return _caller;
    }

    function locker() public view returns (address) {
        return _locker;
    }

    function nextLockId() public view returns (uint256) {
        return _nextLockId;
    }

    function nextClaimId() public view returns (uint256) {
        return _nextCliamId;
    }

    function setCaller(address caller) external onlyGovernance{
        _caller = caller;
    }

    /**
        get lock data list
     */
    function getLockData(uint256 start, uint256 count) public view returns (LockData[] memory records) {
        uint256 end = start.add(count);
        end = Math.min(end,_nextLockId);
        if(start >= end) {
            return records;
        }

        count = end-start;
        uint256 idx = count;
        LockData memory record;
        records = new LockData[](idx);
        for (uint256 i = --end; i >= start; i--) {
            record = _lockTable[i];
            records[count-(idx--)] = record;
            if (i == 0) {
                break;
            }
        }
    }

    /**
        lock amount
     */
    function lock(uint256 amount) external {
        require(_caller == msg.sender, "not caller");

        if(amount > 0) {
            uint256 lockId = _nextLockId++;

            LockData memory lock;
            lock.amount = amount;
            lock.startTime = block.timestamp;
            lock.claimed = false;
            _lockTable[lockId] = lock;

            _balance = _balance.add(amount);
        }
    }

    /**
        claim the lock amount
     */
    function claim(uint256 count) public {
        require(_locker == msg.sender, "not locker");

        uint256 end = _nextCliamId.add(count);
        end = Math.min(end,_nextLockId);

        uint256 claimAmount = 0;
        uint256 i = _nextCliamId;
        for(; i < end; i++) {
            LockData memory lock = _lockTable[i];
            if(lock.startTime + _lockPeriod > block.timestamp){
                break;
            }
            claimAmount = claimAmount.add(lock.amount);
            lock.claimed = true;
            _lockTable[i] = lock;
        }

        _nextCliamId = i;
        if(claimAmount > 0){
            _laika.safeTransfer(msg.sender,claimAmount);
            _balance = _balance.sub(claimAmount);
        }
    }
}
