pragma solidity ^0.6.0;
pragma experimental ABIEncoderV2;


interface ICryptoPunk {
    struct Offer {
        bool isForSale;
        uint punkIndex;
        address seller;
        uint minValue;          // in ether
        address onlySellTo;     // specify to sell only to a specific person
    }

    function punkIndexToAddress(uint punkIndex) external view returns (address);
    function punksOfferedForSale(uint punkIndex) external view returns (Offer memory);
    function buyPunk(uint punkIndex) external payable;
    function transferPunk(address to, uint punkIndex) external;
}

contract UserProxy {

    address private _owner;

    /**
     * @dev Initializes the contract settings
     */
    constructor()
    public
    {
        _owner = msg.sender;
    }

    /**
     * @dev Transfers punk to the smart contract owner
     */
    function transfer(address punkContract, uint256 punkIndex)
    external
    returns (bool)
    {
        if (_owner != msg.sender) {
            return false;
        }

        (bool result,) = punkContract.call(abi.encodeWithSignature("transferPunk(address,uint256)", _owner, punkIndex));

        return result;
    }

}

