pragma solidity 0.6.12;

import '@openzeppelin/contracts/access/Ownable.sol';
import '../interfaces/ISwapFactory.sol';
import './SwapPair.sol';

contract SwapFactory is Ownable, ISwapFactory {
    address public override feeTo;
    address public override feeToSetter;
    address public override freezerSetter;
    address public override migrator;

    mapping(address => mapping(address => address)) public override getPair;
    address[] public override allPairs;

    event PairCreated(address indexed token0, address indexed token1, address pair, uint);

    constructor(address _feeToSetter) public {
        feeToSetter = _feeToSetter;
    }

    function allPairsLength() external override view returns (uint) {
        return allPairs.length;
    }

    function pairCodeHash() external pure returns (bytes32) {
        return keccak256(type(SwapPair).creationCode);
    }

    function createPair(address tokenA, address tokenB) external onlyOwner override returns (address pair) {
        require(tokenA != tokenB, 'ConvergenceX Swap: IDENTICAL_ADDRESSES');
        (address token0, address token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        require(token0 != address(0), 'ConvergenceX Swap: ZERO_ADDRESS');
        require(getPair[token0][token1] == address(0), 'ConvergenceX Swap: PAIR_EXISTS'); // single check is sufficient
        bytes memory bytecode = type(SwapPair).creationCode;
        bytes32 salt = keccak256(abi.encodePacked(token0, token1));
        assembly {
            pair := create2(0, add(bytecode, 32), mload(bytecode), salt)
        }
        SwapPair(pair).initialize(token0, token1);
        getPair[token0][token1] = pair;
        getPair[token1][token0] = pair; // populate mapping in the reverse direction
        allPairs.push(pair);
        emit PairCreated(token0, token1, pair, allPairs.length);
    }

    function setFeeTo(address _feeTo) external override {
        require(msg.sender == feeToSetter, 'ConvergenceX Swap: FORBIDDEN');
        feeTo = _feeTo;
    }

    function setMigrator(address _migrator) external override {
        require(msg.sender == feeToSetter, 'ConvergenceX Swap: FORBIDDEN');
        migrator = _migrator;
    }

    function setFeeToSetter(address _feeToSetter) external override {
        require(msg.sender == feeToSetter, 'ConvergenceX Swap: FORBIDDEN');
        feeToSetter = _feeToSetter;
    }

    function setFreezerSetter(address _freezerSetter) external onlyOwner override {
        freezerSetter = _freezerSetter;
    }

}
