// File: contracts/WasderSale.sol

pragma solidity ^0.6.12;



contract WasderSale is Ownable {
    address public treasury;

    // max amount of erc20 a user can pay
    mapping(address => uint256) public buyers_limits;
    address public token;

    uint256 public startTime;
    uint256 public endTime;

    event Purchased (
        address indexed buyer,
        uint256 amount
    );

    modifier isOpenForDeposits(){
        require(block.timestamp > startTime && block.timestamp < endTime, "Closed for deposits!");
        _;
    }

    constructor(address _treasury, uint256 _startTime, uint256 _endTime, address _token) public {
        treasury = _treasury;
        startTime = _startTime;
        endTime = _endTime;
        token = _token;
    }

    function addAllocations(address[] memory _buyer, uint256[] memory _amount) external onlyOwner {
        require(_buyer.length == _amount.length, "Arrays have different lengths!");

        for (uint i = 0; i < _buyer.length; i++){
            buyers_limits[_buyer[i]] = _amount[i];
        }
    }

    function setTreasury(address _treasury) external onlyOwner {
        treasury = _treasury;
    }
    function setStartTime(uint256 _startTime) external onlyOwner {
        startTime = _startTime;
    }
    function setEndTime(uint256 _endTime) external onlyOwner {
        endTime = _endTime;
    }
    function setToken(address _token) external onlyOwner {
        token = _token;
    }

    function adminWithdrawERC20(address _token) external onlyOwner{
        uint256 amount = IERC20(_token).balanceOf(address(this));
        IERC20(_token).transfer(msg.sender, amount);
    }

    function adminWithdraw() external onlyOwner {
        (bool success, ) = msg.sender.call{ value: address(this).balance }("");
        require(success, "Transfer failed.");
    }

    function deposit(uint256 _amount) external isOpenForDeposits {
        require(_amount <= buyers_limits[msg.sender], "Limit exceeded");
        buyers_limits[msg.sender] -= _amount;
        IERC20(token).transferFrom(msg.sender, treasury, _amount);
        emit Purchased(msg.sender, _amount);
    }
}
