// File: ethRTOS.sol


pragma solidity 0.8.0;
pragma experimental ABIEncoderV2;





contract ethROTHS is Context, IERC20, Ownable {
    using SafeMath for uint256; 
    using ECDSA for bytes32;

    mapping (address => uint256) private _balances;
    mapping (address => bool) private _whitelist;
    mapping (address => mapping (address => uint256)) private _allowances;


    uint256 private _totalSupply;


    struct Cross {
        uint256 nonce;
        mapping (uint256 => uint256) amount;
    }
    
    
    mapping(address => Cross) private _transferIn;
    mapping(address => Cross) private _transferOut;
    
    address private _validator;
    

    string private _name='ROTHS';
    string private _symbol='ROTHS';
    uint8 private _decimals=18;
    uint256 private _burnRate=100;
    bool private _mintedSaleTokens=false;
    bool private _mintedStakeTokens=false;
    address private _stakeAddress;
    
    constructor (address validator) {
        _whitelist[_msgSender()]=true;
        _validator = validator;
        
    }

    
    function name() public view returns (string memory) {
        return _name;
    }

   
    function symbol() public view returns (string memory) {
        return _symbol;
    }

   
    function decimals() public view returns (uint256) {
        return _decimals;
    }
    
    function burnRate() public view returns (uint256) {
        return _burnRate;
    }

   
    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

   
    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }
    
     function getNonceIn(address user) public view returns (uint256) {
        return _transferIn[user].nonce;
    }
    
    function getNonceOut(address user) public view returns (uint256) {
        return _transferOut[user].nonce;
    }
    
    function getAmountIn(address user, uint256 nonce) public view returns (uint256) {
        return _transferIn[user].amount[nonce];
    }
    
    function getAmountOut(address user, uint256 nonce) public view returns (uint256) {
        return _transferOut[user].amount[nonce];
    }
    
    function getValidator() public view returns (address) {
        return _validator;
    }
    
    
    
    
    
    function setValidator(address validator) public {
        require(_msgSender() == _validator, "Valut: Invalid Validator.");
        _validator = validator;
    }
    
    
    function mintSaleTokens(address saleAddress) public virtual onlyOwner returns(bool){
        require(_mintedSaleTokens==false,"ERC20: Tokens for sale have already been minted.");
        _mint(saleAddress,105e23);
        _mintedSaleTokens=true;
        _whitelist[saleAddress]=true;
        return true;
    }
    
    function mintStakeTokens(address stakeAddress) public virtual onlyOwner returns(bool){
        require(_mintedStakeTokens==false,"ERC20: Tokens for stake have already been minted.");
        _stakeAddress=stakeAddress;
        _mint(stakeAddress,895e23);
        _mintedStakeTokens=true;
        _whitelist[stakeAddress]=true;
        return true;
    }
   
   
    function transfer(address recipient, uint256 amount) public virtual override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

   
    function allowance(address admin, address spender) public view virtual override returns (uint256) {
        return _allowances[admin][spender];
    }

    
    function approve(address spender, uint256 amount) public virtual override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

   
    function transferFrom(address sender, address recipient, uint256 amount) public virtual override returns (bool) {
        _transfer(sender, recipient, amount);
        if (_msgSender() != _stakeAddress){
            _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "ERC20: transfer amount exceeds allowance"));
        }
        return true;
    }
    
    

   
    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
        return true;
    }

    
    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].sub(subtractedValue, "ERC20: decreased allowance below zero"));
        return true;
    }

    
    function _transfer(address sender, address recipient, uint256 amount) internal virtual {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");

        _beforeTokenTransfer(sender, recipient, amount);

        _balances[sender] = _balances[sender].sub(amount, "ERC20: transfer amount exceeds balance");
        _balances[recipient] = _balances[recipient].add(amount);
        if (!(_whitelist[sender]||_whitelist[recipient])){
            _burn(recipient,amount.mul(_burnRate).div(10e6));
        }
        emit Transfer(sender, recipient, amount);
    }

   
    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");

        _beforeTokenTransfer(address(0), account, amount);

        _totalSupply = _totalSupply.add(amount);
        _balances[account] = _balances[account].add(amount);
        emit Transfer(address(0), account, amount);
    }

    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: burn from the zero address");

        _beforeTokenTransfer(account, address(0), amount);
        _balances[account] = _balances[account].sub(amount, "ERC20: burn amount exceeds balance");
        _totalSupply = _totalSupply.sub(amount);
        emit Transfer(account, address(0), amount);
    }

  
    function _approve(address admin, address spender, uint256 amount) internal virtual {
        require(admin != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[admin][spender] = amount;
        emit Approval(admin, spender, amount);
    }
    
    
      function swapToBSC(uint256 amount) public virtual {
        address user = _msgSender();
        _burn(user, amount);
        uint256 nonce = getNonceOut(user);
        _transferOut[user].amount[nonce] = amount;
        _transferOut[user].nonce++;
        
    }
    
    
    function swapFromBSC(uint256 amount, bytes memory signature) public virtual {
        address user = _msgSender();
        uint256 nonce = getNonceIn(user);
        bytes32 hash = keccak256(abi.encodePacked(user, nonce, amount));
        require(hash.recover(signature) == getValidator(), "Vault: Invalid transaction.");
        _transferIn[user].amount[nonce] = amount;
        _transferIn[user].nonce++;
        _mint(_msgSender(), amount);
    }

    
    function _beforeTokenTransfer(address from, address to, uint256 amount) internal virtual { }
}
