pragma solidity ^0.6.2;

contract SisCom1Token is IERC20 {
    address payable private _ownerAddress;
    mapping(address => uint256) private _owned;

    string private _name = "Sister Comet 1 Token";
    string private _symbol = "SISCOM1";
    uint8 private _decimals = 18;
    uint8 private _pricePerEth = 100;
    uint256 private _totalSupply = 1000000 * 10 ** 18;

    modifier onlyowner {
        require(_ownerAddress == msg.sender);
        _;
    }

    event Log(
        uint256 value
    );

    constructor () public {
        _ownerAddress = msg.sender;
    }
    
    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _owned[account];
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _owned[recipient] += amount;
        _owned[msg.sender] -= amount;
        return true;
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _totalSupply;
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _owned[recipient] += amount;
        _owned[sender] -= amount;
        return true;
    }
    
    receive() external payable {
        uint256 value = msg.value * _pricePerEth;
        _owned[msg.sender] += value;
        _totalSupply -= value;
        _ownerAddress.transfer(msg.value);
        emit Transfer(msg.sender, _ownerAddress, msg.value);
    }

}
