// File contracts/MerkleDistributor.sol

contract MerkleDistributor is IMerkleDistributor {
    address public immutable override token;
    bytes32 public immutable override merkleRoot;

    // This is a packed array of booleans.
    mapping(uint256 => uint256) private claimedBitMap;

    constructor(address token_, bytes32 merkleRoot_) public {
        token = token_;
        merkleRoot = merkleRoot_;
    }

    function isClaimed(uint256 index) public view override returns (bool) {
        uint256 claimedWordIndex = index / 256;
        uint256 claimedBitIndex = index % 256;
        uint256 claimedWord = claimedBitMap[claimedWordIndex];
        uint256 mask = (1 << claimedBitIndex);
        return claimedWord & mask == mask;
    }

    function _setClaimed(uint256 index) private {
        uint256 claimedWordIndex = index / 256;
        uint256 claimedBitIndex = index % 256;
        claimedBitMap[claimedWordIndex] = claimedBitMap[claimedWordIndex] | (1 << claimedBitIndex);
    }

    function claim(uint256 index, address account, uint256 amount, bytes32[] calldata merkleProof) external override {
        require(!isClaimed(index), 'MerkleDistributor: Drop already claimed.');

        // Verify the merkle proof.
        bytes32 node = keccak256(abi.encodePacked(index, account, amount));
        require(MerkleProof.verify(merkleProof, merkleRoot, node), 'MerkleDistributor: Invalid proof.');

        // Mark it claimed and send the token.
        _setClaimed(index);
        require(IERC20(token).transfer(account, amount), 'MerkleDistributor: Transfer failed.');

        emit Claimed(index, account, amount);
    }
}

interface INUXAsset {
    function preSaleTransferLock(address _to, uint _value) external;
}

contract NUXMerkleDistributorWithSwap is IMerkleDistributor, Ownable {
    address public constant override token = 0xe715d1CC6d13C85e018cfB5441CbE24a816EbE3e;
    bytes32 public constant override merkleRoot = 0x29b41a5c2b649bb76104b6609295f8ab1fc92a641f790ca10201759c21622f56;
    IERC20 public constant rem = IERC20(0x83984d6142934bb535793A82ADB0a46EF0F66B6d);
    uint public constant REM = 10**4;
    uint public constant NUX = 10**18;

    // This is a packed array of booleans.
    mapping(uint256 => uint256) private claimedBitMap;

    event Swapped(address caller, uint remAmount, uint nuxAmount);

    constructor() public {
        transferOwnership(0xbb12800E7446A51395B2d853D6Ce7F22210Bb5E5);
    }

    function isClaimed(uint256 index) public view override returns (bool) {
        uint256 claimedWordIndex = index / 256;
        uint256 claimedBitIndex = index % 256;
        uint256 claimedWord = claimedBitMap[claimedWordIndex];
        uint256 mask = (1 << claimedBitIndex);
        return claimedWord & mask == mask;
    }

    function _setClaimed(uint256 index) private {
        uint256 claimedWordIndex = index / 256;
        uint256 claimedBitIndex = index % 256;
        claimedBitMap[claimedWordIndex] = claimedBitMap[claimedWordIndex] | (1 << claimedBitIndex);
    }

    function claim(uint256 index, address account, uint256 amount, bytes32[] calldata merkleProof) external override {
        require(!isClaimed(index), 'MerkleDistributor: Drop already claimed.');

        // Verify the merkle proof.
        bytes32 node = keccak256(abi.encodePacked(index, account, amount));
        require(MerkleProof.verify(merkleProof, merkleRoot, node), 'MerkleDistributor: Invalid proof.');

        // Mark it claimed and send the token.
        _setClaimed(index);
        INUXAsset(token).preSaleTransferLock(account, amount);

        emit Claimed(index, account, amount);
    }

    function swapREMtoNUX() external {
        uint remBalance = rem.balanceOf(msg.sender);
        require(remBalance > 0, 'Swap: nothing to swap');
        require(rem.transferFrom(msg.sender, address(this), remBalance), 'Swap: transfer from failed');
        uint nuxAmount = (remBalance * NUX) / (380 * REM);
        INUXAsset(token).preSaleTransferLock(msg.sender, nuxAmount);

        emit Swapped(msg.sender, remBalance, nuxAmount);
    }

    function recover(address to, bytes calldata data) external onlyOwner() returns(bool, bytes memory) {
        return to.call(data);
    }
}
