// File contracts/VotingShare.sol


pragma solidity ^0.8.0;



struct LockAmount {
    uint256 unlockTime;
    uint256 amount;
}

interface IERC20 {
    function totalSupply() external view returns (uint256);

    function balanceOf(address account) external view returns (uint256);
}

interface ITun {
    function borPledgeInfo(address user) external view returns (uint256);

    function lockInfo(address user, uint256 index)
        external
        view
        returns (LockAmount memory);

    function userLockLength(address account) external view returns (uint256);
}

interface IUniswapV2Pair {
    function decimals() external pure returns (uint8);

    function totalSupply() external view returns (uint256);

    function balanceOf(address owner) external view returns (uint256);

    function getReserves()
        external
        view
        returns (
            uint112 reserve0,
            uint112 reserve1,
            uint32 blockTimestampLast
        );
}

contract VotingShare is UUPSUpgradeable, OwnableUpgradeable {
    IERC20 constant bor = IERC20(0x3c9d6c1C73b31c837832c72E04D3152f051fc1A9);
    IERC20 constant boring = IERC20(0xBC19712FEB3a26080eBf6f2F7849b417FdD792CA);
    // ITun constant tunnel = ITun(0x258a1eb6537Ae84Cf612f06B557B6d53f49cC9A1);
    // goldRush contract for boring
    IERC20 constant gr = IERC20(0xd54D2e3024D5E5Cabab32770b3ab2601139Aba56);

    IUniswapV2Pair constant uniBor =
        IUniswapV2Pair(0xc9ca10d36441B5b45d5E63480139105f037972e0);
    IUniswapV2Pair constant sushiBor =
        IUniswapV2Pair(0x44D34985826578e5ba24ec78c93bE968549BB918);

    ITun[] public tunnels;

    function _authorizeUpgrade(address) internal override onlyOwner {}

    function initialize(address[] memory _tunnels) public initializer {
        for (uint256 i; i < _tunnels.length; i++) {
            tunnels.push(ITun(_tunnels[i]));
        }
    }

    function addTunnel(address _tunnel) external onlyOwner {
        tunnels.push(ITun(_tunnel));
    }

    function decimals() external pure returns (uint8) {
        return uint8(18);
    }

    function name() external pure returns (string memory) {
        return "BoringDAO Voting Share";
    }

    function symbol() external pure returns (string memory) {
        return "BoringDAO VS";
    }

    function totalSupply() external view returns (uint256) {
        return bor.totalSupply();
    }

    function uni(address account) public view returns (uint256) {
        (uint112 borAmount, , ) = uniBor.getReserves();
        uint256 lpAccount = uniBor.balanceOf(account);
        uint256 lpTotal = uniBor.totalSupply();
        return (uint256(borAmount) * lpAccount) / lpTotal;
    }

    function sushi(address account) public view returns (uint256) {
        (uint112 borAmount, , ) = sushiBor.getReserves();
        uint256 lpAccount = sushiBor.balanceOf(account);
        uint256 lpTotal = sushiBor.totalSupply();
        return (uint256(borAmount) * lpAccount) / lpTotal;
    }

    function tunnelBor(address _voter) public view returns (uint256) {
        uint totalBor;
        for (uint256 j; j < tunnels.length; j++) {
            ITun tunnel = tunnels[j];
            uint256 bor2 = tunnel.borPledgeInfo(_voter);
            uint256 lock;
            uint256 unlock;
            for (uint256 i; i < tunnel.userLockLength(_voter); i++) {
                if (block.timestamp >= tunnel.lockInfo(_voter, i).unlockTime) {
                    unlock = unlock + tunnel.lockInfo(_voter, i).amount;
                } else {
                    lock = lock + tunnel.lockInfo(_voter, i).amount;
                }
            }
            totalBor = totalBor + bor2 + lock + unlock;
        }
        return totalBor * 10000;
    }

    function goldRush(address _voter) public view returns (uint) {
        return gr.balanceOf(_voter);
    }

    function balanceOf(address _voter) external view returns (uint256) {
        // uint256 bor1 = bor.balanceOf(_voter) / 2;
        uint boring1 = boring.balanceOf(_voter);
        uint256 bor2 = tunnelBor(_voter);
        uint boring2 = goldRush(_voter);    
        // uint256 bor3 = uni(_voter);
        // uint256 bor4 = sushi(_voter);

        return boring1+bor2+boring2;
    }
