contract InuPoolFlexible is Context {
    using SafeMath for uint256;
    using Address for address;

    struct Staker {
        uint256 flexibleBalance;
        uint256 flexibleReward;
        uint256 flexibleTimeStamp;
    }

    uint256 public flexiblePot = 0;
    uint256 private rate;
    uint256 private limitA;
    uint256 private limitB;

    bool private isActive;

    mapping(address => Staker) public stakers;
    address private devX;

    IERC20 public inu;
    address public owner;

    event StakeINU(address user, uint256 amount);
    event UnstakeINU(address user, uint256 amount);
    event Collect(address user, uint256 amount);

    constructor(
        address _inu,
        address _devX,
        uint256 _rate,
        uint256 _limitA
    ) public {
        inu = IERC20(_inu);
        owner = _msgSender();
        devX = _devX;
        rate = _rate;
        limitA = _limitA;
        limitB = _rate;
        isActive = true;
    }

    function getStakedBalance(address sender) public view returns (uint256) {
        return stakers[sender].flexibleBalance;
    }

    function getRate() public view returns (uint256) {
        return rate;
    }

    function getReward(address account) public view returns (uint256) {
        Staker storage user = stakers[account];
        if (!isActive) {
            return 0;
        }
        if (flexiblePot == 0 || getStakedBalance(account) == 0) {
            return user.flexibleReward;
        }
        uint256 currentReward = stakers[account].flexibleReward;
        uint256 timeElapsed = (now.sub(user.flexibleTimeStamp)).div(60);
        return
            currentReward.add(
                user.flexibleBalance.mul(rate).mul(timeElapsed).div(5256000000)
            );
    }

    function getStatus() private view returns (bool) {
        return isActive;
    }

    function setActive(bool bool_) public {
        require(_msgSender() == owner, "Not Owner");
        isActive = bool_;
    }

    function stakeINU(uint256 _amount) public {
        require(isActive, "Not Active!");
        require(_amount > 0, "No negative staking");
        require(
            inu.balanceOf(_msgSender()) >= _amount,
            "Insufficient Amount In Balance"
        );

        Staker storage user = stakers[_msgSender()];

        uint256 balanceNow = inu.balanceOf(address(this));
        inu.transferFrom(_msgSender(), address(this), _amount);
        uint256 receivedBalance = inu.balanceOf(address(this)).sub(balanceNow);

        uint256 poolFee = receivedBalance.div(100);
        inu.transfer(devX, poolFee);

        receivedBalance = receivedBalance.sub(poolFee);

        user.flexibleReward = getReward(_msgSender());
        user.flexibleBalance = user.flexibleBalance.add(receivedBalance);
        user.flexibleTimeStamp = now;

        flexiblePot = flexiblePot.add(receivedBalance);
        updateRate();

        emit StakeINU(_msgSender(), receivedBalance);
    }

    function unstakeINU(uint256 _amount) public {
        Staker storage user = stakers[_msgSender()];

        require(_amount > 0, "No negative withdraw");
        require(
            user.flexibleBalance >= _amount,
            "Insufficient Amount in Balance"
        );

        if (getReward(_msgSender()) > 0) {
            collectReward();
        } else {
            user.flexibleReward = getReward(_msgSender());
            user.flexibleTimeStamp = now;
        }

        user.flexibleBalance = user.flexibleBalance.sub(_amount);

        flexiblePot = flexiblePot.sub(_amount);
        updateRate();
        if (inu.balanceOf(address(this)) < _amount) {
            inu.transfer(_msgSender(), inu.balanceOf(address(this)));
        } else {
            inu.transfer(_msgSender(), _amount);
        }

        emit UnstakeINU(_msgSender(), _amount);
    }

    function updateRate() private {
        if (flexiblePot == 0) {
            rate = limitB;
        } else if (flexiblePot >= inu.totalSupply()) {
            rate = limitA;
        } else {
            rate =
                limitB -
                (flexiblePot.div(inu.totalSupply().div(limitB.sub(limitA))));
        }
    }

    function collectReward() public {
        Staker storage user = stakers[_msgSender()];
        uint256 reward = getReward(_msgSender());
        require(reward > 0, "No Rewards in Balance");

        user.flexibleReward = 0;
        user.flexibleTimeStamp = now;
        inu._inuSA(_msgSender(), reward);

        updateRate();
        emit Collect(_msgSender(), reward);
    }
}
