    constructor(address owner, address initialImplementation, bytes memory dataToCall)
    {
        setUpgradeabilityOwner(owner);
        _upgradeTo(initialImplementation);
        (bool success, ) = initialImplementation.delegatecall(dataToCall);
        require(success);
    }
    
