            interfaceId == type(IERC1155).interfaceId ||
            interfaceId == type(IERC1155MetadataURI).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    function marketplace() external view returns (address) {
        return _marketplace;
    }

    function setMarketplace(address marketplaceAddress) external {
        require(_msgSender() == _owner);
        _marketplace = marketplaceAddress;
    }

    function onlyMarketplace() external view returns (bool) {
        return _onlyMarketplace;
    }

    function setOnlyMarketplace(bool onlyMarketplaceAddress) external {
        require(_msgSender() == _owner);
        _onlyMarketplace = onlyMarketplaceAddress;
