contract SatoshiART1155 is ERC1155, AccessControl {
    using Counters for Counters.Counter;

    Counters.Counter private _tokenIds;

    struct Token {
        address creator;
        uint256 royalty;
    }
    mapping(uint256 => Token) private _token;

    bytes32 public constant CREATOR_ROLE = keccak256("CREATOR_ROLE");

    bool private _anyAddressCanCreateItem = false;

    event ApprovalForItemCreator(
        address contractOwner,
        address itemCreator,
        bool approved
    );

    constructor()
        ERC1155(
            "www.satoshi.art/api/public/tokens/{id}"
        )
    {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC1155, AccessControl)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    function setAnyAddressCanCreateItem(bool isAnyAddressCanCreateItem) public {
        require(
            hasRole(DEFAULT_ADMIN_ROLE, _msgSender()),
            "Caller is not an admin"
        );
        _anyAddressCanCreateItem = isAnyAddressCanCreateItem;
    }

    function anyAddressCanCreateItem() public view returns (bool) {
        return _anyAddressCanCreateItem;
    }

    function createItem(
        uint256 amount,
        uint256 royalty //multiplier:10000
    ) external returns (uint256[] memory) {
        require(
            _anyAddressCanCreateItem || hasRole(CREATOR_ROLE, _msgSender()),
            "Address is not approved for creating new item."
        );
        require(royalty <= 1000, "Royalty is too high");

        uint256[] memory newItemIds = new uint256[](amount);
        uint256[] memory amounts = new uint256[](amount);
        for (uint256 i = 0; i < amount; i++) {
            _tokenIds.increment();
            newItemIds[i] = _tokenIds.current();
            amounts[i] = 1;
        }
        _mintBatch(_msgSender(), newItemIds, amounts, "");

        for (uint256 i = 0; i < newItemIds.length; i++) {
            _token[newItemIds[i]] = Token({
                creator: _msgSender(),
                royalty: royalty
            });
        }

        return newItemIds;
    }

    function tokenCreator(uint256 _id) external view returns (address) {
        return _token[_id].creator;
    }

    function tokenRoyalty(uint256 _id) external view returns (uint256) {
        return _token[_id].royalty;
    }
}
